import { useState, useContext, Fragment, useEffect } from 'react'
import { Link } from 'react-router-dom'
import { Facebook, Twitter, Mail, GitHub, Edit2} from 'react-feather'
import InputPasswordToggle from '@components/input-password-toggle'
import {Col, Row, Card, CardBody, CardTitle, CardText, Form, FormGroup, UncontrolledTooltip, Label, Input, CustomInput, Button } from 'reactstrap'
import { useForm } from 'react-hook-form'
import classnames from 'classnames'
import { useDispatch } from 'react-redux'
import { addStateMaster, updateStateMaster } from '@store/actions/masteruser'
import axios from 'axios'
// import '@styles/base/pages/page-auth.scss'
import { makeStyles } from '@material-ui/core/styles'
import Paper from '@material-ui/core/Paper'
import Table from '@material-ui/core/Table'
import TableBody from '@material-ui/core/TableBody'
import TableCell from '@material-ui/core/TableCell'
import TableContainer from '@material-ui/core/TableContainer'
import TableHead from '@material-ui/core/TableHead'
import TablePagination from '@material-ui/core/TablePagination'
import TableRow from '@material-ui/core/TableRow'
const loginAuthID = sessionStorage.getItem('id_token')
const columns = [
  { id: 'index', label: 'Sr.No.', minWidth: 10 },
  {
    id: 'code',
    label: 'State Code',
    minWidth: 170,
    align: 'right',
    format: (value) => value.toLocaleString('en-US')
  },
  {
    id: 'name',
    label: 'State Name',
    minWidth: 170,
    align: 'right',
    format: (value) => value.toLocaleString('en-US')
  },
  
  {
    id: 'size',
    label: 'Country',
    minWidth: 170,
    align: 'right',
    format: (value) => value.toLocaleString('en-US')
  },
  { id: 'action', label: 'Action', minWidth: 100 }
]


const useStyles = makeStyles({
  root: {
    width: '100%'
  }
})
const MasterForm = (props) => {
    const { register, errors, setValue, handleSubmit } = useForm({defaultValues:props ? props && props.preloadValue && props.preloadValue.detail : ""})
    const [email, setEmail] = useState('')
    const [country, setCountry] = useState([])
    const dispatch = useDispatch()
    const [state, setState] = useState([])
    const [updatecountry, setUpdatecountry] = useState(props && props.preloadValue && props.preloadValue.detail.country_id.country_name)
    const [countryid, setCountryid] = useState('')
// console.log("country id", countryid)
useEffect(() => {
  setValue("state_id", props && props.preloadValue && props.preloadValue.detail.country_id._id)
}, [props])

  const statebycountryid = 'http://172.105.49.15:3030/api/master/get_state_by_country_id'
  useEffect(async() => {
    if (countryid) {
    await axios.post(statebycountryid, {
      country_id:countryid
    }, {
      headers:{
        token:loginAuthID
      }
    })
    .then(r => setState(r))
  }
  })


    const countryname = 'http://172.105.49.15:3030/api/master/get_country_list'
    useEffect(() => {
      axios.get(countryname, {
        headers:{
          token:loginAuthID
        }
      })
      .then(r => setCountry(r))
    }, [setCountry])

    // console.log(state)
    const onSubmit = data => {
        // alert("SUBMITED SUCCESSFULLY")
        if (props && props.preloadValue) { 
          dispatch(updateStateMaster(data, props))
        } else {
          dispatch(addStateMaster(data, props))
        }
      }

    console.log(props)
return (
  <div>
 <Form className='auth-login-form mt-2' onSubmit={handleSubmit(onSubmit)}>
            <Row>
            <Col md={4}>
              <FormGroup>
        <Label for="exampleText" >Country</Label>
        <Input type="select" name="state_id"
        value={updatecountry}
        onChange={(e) => setCountryid(e.target.value)}
                 className={classnames({ 'is-invalid': errors['state_id'] })}
                 innerRef={register({ required: true, validate: value => value !== '' })}
        id="exampleSelect">
         {country && country.data && country.data.data && country.data.data.map((countries, index) => {
           return (
            <option value={countries._id}>{countries.country_name}</option>
           )
         })} 
        </Input>
      </FormGroup>
      {errors.state_id && errors.state_id.type === 'required' && <p style={{color:"red"}}>State id is required</p>}

      </Col>
            <Col md={4}>
            <FormGroup>
                <Label className='form-label' for='login-email'>
                 State Code
                </Label>
                <Input
                 type='number'
                 id='code'
                name="state_code"
                  className={classnames({ 'is-invalid': errors['state_code'] })}
                  innerRef={register({ required: true, validate: value => value !== '' })}
                 placeholder='Enter state code' autoFocus />
              </FormGroup>
      {errors.state_code && errors.state_code.type === 'required' && <p style={{color:"red"}}>State code is required</p>}

              </Col>

              <Col md={4}>
              <FormGroup>
                <Label className='form-label' for='login-email'>
                  State Name
                </Label>
                <Input
                 type='text'
                //  value={email}
                 id='state_name'
                 name='state_name'
                //  onChange={e => setEmail(e.target.value)}
                  className={classnames({ 'is-invalid': errors['state_name'] })}
                  innerRef={register({ required: true, validate: value => value !== '' })}
                placeholder='Enter state Name'
                 autoFocus/>
        {/* {state && state.data && state.data.data && state.data.data.map((statebycountry, index) => {
           return (
            <option value={statebycountry._id}>{statebycountry.state_name}</option>
           )
         })} 
         </Input> */}
              </FormGroup>
      {errors.state_name && errors.state_name.type === 'required' && <p style={{color:"red"}}>State name is required</p>}

              </Col>

              
              <Col md={12}>
              <span className="input-group-btn">
          <Button.Ripple  type="submit" className='ripple-button  btn-theam theam_m' size="sm" style={{float:"right"}}>
          { props.preloadValue ? 'Update' : 'Submit' }
              </Button.Ripple>
              </span>
              </Col>
              </Row>
              {/* { props.preloadValue ?    <Button.Ripple color='primary' type="submit" className='ripple-button' size="sm" style={{float:"right"}} >
               Update
              </Button.Ripple> :   <Button.Ripple color='primary' type="submit" className='ripple-button' size="sm" style={{float:"right"}} >
                Save
              </Button.Ripple>} */}
            
            </Form>

  </div>
)
}

const StateMaster = (props) => {
  const [addform, setAddForm] = useState(false)
  const [updateuser, setUpdateuser] = useState(false)
  const [addbutton, setAddButton] = useState(false)
  const [userdetail, setUserDetail] = useState()
  const [user, setUser] = useState([])
  const classes = useStyles()
  const [page, setPage] = useState(0)
  const [rowsPerPage, setRowsPerPage] = useState(10)
  const [searching, setSearchStream] = useState('')
  const handleChangePage = (event, newPage) => {
    setPage(newPage)
  }

  const handleChangeRowsPerPage = (event) => {
    setRowsPerPage(+event.target.value)
    setPage(0)
  }
 ///search table data
const searchChange = (e) => {
 e.preventDefault()
 const searchstream = e.target.value
//  setSearchStream(searchstream)
//  const searchString = searching
if (searchstream.length > 0) {
  const newstring = new RegExp(searchstream)
       const searchitem = userdetail && userdetail.data && userdetail.data.data && userdetail.data.data.filter(item => {
         console.log(item)
         return item.state_name.match(searchstream) || item.state_code.match(newstring) 
       })
       console.log(searchitem)
       setUserDetail({data:{data:searchitem}})
      } else {
        setUserDetail(user)
      }
}
  const onAddClick = () => {
    setAddForm(true)
    setAddButton(true)
    setUpdateuser(false)
  }
  const onCancleClick = () => {
    setAddForm(false)
    setAddButton(false)
  }

  const onEditClick = (detail) => {
    props.history.push({
      state : {
     detail
     }})
     setUpdateuser(true)
     setAddForm(false)
  }
  const userdetailapi = 'http://172.105.49.15:3030/api/master/get_state_list'
  useEffect(() => {
    axios.get(userdetailapi, {
      headers:{
        token:loginAuthID
      }
    })
    .then(r => { 
      setUserDetail(r)
      setUser(r)
    })
  }, [userdetailapi, addform, updateuser])
console.log(userdetail)
  return (
    <div className='auth-wrapper auth-v1 px-2'>
      <div className='auth-inner py-2'>
      { addform === true ?   <Card className='mb-0'>
          <CardBody>
            <CardTitle tag='h4' className='mb-1'>
             Add State 
            </CardTitle>
            {/* <CardText className='mb-2'>Please Register for master</CardText> */}
           <MasterForm onAdd={() => { setAddForm(false) }} onMasterSave={() => setAddButton(false)}/>
          </CardBody>
        </Card> : " "}
        {updateuser === true ?   <Card className='mb-0'>
          <CardBody>
            <CardTitle tag='h4' className='mb-1'>
           Update State 
            </CardTitle>
            {/* <CardText className='mb-2'>Please Register for master</CardText> */}
            <MasterForm
             preloadValue={props && props.location && props.location.state} onAdd={() => { setUpdateuser(false) }}
             onMasterSave={() => setAddButton(false)}
             />
          </CardBody>
        </Card> : "" }
      
        <div style={{padding:10}}>
     
        </div>
        <Card className='mb-0'>
          <CardBody> 
          <Row>
              <Col md={3}>
              <h4 style={{margin:'10px'}} className='mb-1'>State List</h4>
              </Col>
              <Col md={2}>
                  <h6 style={{float:"right", marginTop:"10px"}}>Search</h6>
                  </Col>
              <Col md={3}>
            <FormGroup>
                <Input 
                type='search'
                onChange ={(e) => {
                  searchChange(e)
                }}
                id='code'
                name="image"
                //   className={classnames({ 'is-invalid': errors['image'] })}
                //   innerRef={register({ required: true, validate: value => value !== '' })}
                 placeholder='search...' autoFocus />
              </FormGroup>
              </Col>
              <Col md={4}>
              {addbutton === false ?   <Button.Ripple className='ripple-button' type="submit" size="sm" onClick={onAddClick} style={{margin:'10px', float:"right"}}>
          <i class="fa fa-plus"></i> Add State
              </Button.Ripple> : <Button.Ripple className='ripple-button' type="submit" size="sm" onClick={onCancleClick} style={{margin:'10px', float:"right"}}>
          <i class="fa fa-minus"></i> Add State
              </Button.Ripple>
              
            }
              </Col>
            </Row>
        
    <TableContainer>
        <Table stickyHeader size="small" aria-label="a dense table">
          <TableHead>
            <TableRow>
              {columns.map((column) => (
                <TableCell
                  key={column.id}
                  align={column.align}
                  style={{ minWidth: column.minWidth }}
                >
                  {column.label}
                </TableCell>
              ))}
            </TableRow>
          </TableHead>
          <TableBody>
            {userdetail && userdetail.data && userdetail.data.data && userdetail.data.data.slice((page * rowsPerPage), (page * rowsPerPage) + rowsPerPage).map((detail, index) => {
              console.log(detail)
              return (
                <TableRow hover role="checkbox" tabIndex={-1}>
                      <TableCell >
                    {index + 1}
                      </TableCell>
                      <TableCell  >
                      {detail.state_code}
                      </TableCell>
                      <TableCell  >
                      {detail.state_name}
                      </TableCell>
                      <TableCell  >
                      {detail.country_id && detail.country_id.country_name}
                      </TableCell>
                      <TableCell >
                      <Button.Ripple className='btn-icon btn-sm rounded-circle' color='flat-success'  onClick={() => onEditClick(detail, props)} outline id={`positionTop_${index + 1}`}>
                        <Edit2 size={16} />
                      </Button.Ripple>
                      <UncontrolledTooltip placement='top' target={`positionTop_${index + 1}`}>
                      Edit
                      </UncontrolledTooltip>
                      </TableCell>
                </TableRow>
              )
            })}
          </TableBody>
        </Table>
      </TableContainer>
      <TablePagination
        rowsPerPageOptions={[10, 20, 30]}
        component="div"
        count={userdetail && userdetail.data && userdetail.data.data && userdetail.data.data.length}
        rowsPerPage={rowsPerPage}
        page={page}
        onPageChange={handleChangePage}
        onRowsPerPageChange={handleChangeRowsPerPage}
      />
  
    </CardBody>
    </Card>
      </div>
    </div>
  )
}

export default StateMaster
