import {React, useState, useContext, Fragment, useEffect } from 'react'
import { Facebook, Twitter, Mail, GitHub, Edit2 } from 'react-feather'
import {Col, Row, Card, CardBody, CardTitle, CardText, Form, FormGroup, UncontrolledTooltip, Label, Input, Tooltip, InputGroup, InputGroupAddon, ListGroup, ListGroupItem, InputGroupText, CustomInput, Button } from 'reactstrap'
const moment = require('moment')
import { useForm, Controller } from 'react-hook-form'
import classnames from 'classnames'
import { useDispatch } from 'react-redux'
import { add_Stationary_Request, update_Stationary_Request } from '@store/actions/purchase/stationaryRequest'
import axios from 'axios'
import '@styles/base/pages/page-auth.scss'
import { makeStyles, useTheme  } from '@material-ui/core/styles'
import useMediaQuery from '@material-ui/core/useMediaQuery'
import 'bootstrap/dist/css/bootstrap.min.css'
// import InputAdornment from "@material-ui/core/InputAdornment"
import makeAnimated from 'react-select/animated'
import Divider from '@material-ui/core/Divider'
import Icon from '@material-ui/core/Icon'
import { green, red } from '@material-ui/core/colors'
import Switch from '@material-ui/core/Switch'
import Table from '@material-ui/core/Table'
import TableBody from '@material-ui/core/TableBody'
import TableCell from '@material-ui/core/TableCell'
import TableContainer from '@material-ui/core/TableContainer'
import TableHead from '@material-ui/core/TableHead'
import TablePagination from '@material-ui/core/TablePagination'
import TableRow from '@material-ui/core/TableRow'
import axiosConfig from './../../../axiosConfig'

const loginAuthID = sessionStorage.getItem('id_token')
const columns = [
  { id: 'index', label: 'Sr. No.', minWidth: 100 },
  {
    id: 'Location',
    label: 'Location',
    minWidth: 170,
    align: 'right',
    format: (value) => value.toLocaleString('en-US')
  },
  {
    id: 'Department',
    label: 'Department',
    minWidth: 170,
    align: 'right',
    format: (value) => value.toLocaleString('en-US')
  },
  {
    id: 'EmployeeName',
    label: 'Employee Name',
    minWidth: 170,
    align: 'right',
    format: (value) => value.toLocaleString('en-US')
  },
  {
    id: 'required_date',
    label: 'Required date',
    minWidth: 170,
    align: 'right',
    format: (value) => value.toLocaleString('en-US')
  },
  {
    id: 'item_code',
    label: 'item code',
    minWidth: 170,
    align: 'right',
    format: (value) => value.toLocaleString('en-US')
  },
    {
    id: 'uom',
    label: 'uom',
    minWidth: 170,
    align: 'right',
    format: (value) => value.toLocaleString('en-US')
  },
  {
    id: 'item_Discription',
    label: 'Item Discription',
    minWidth: 170,
    align: 'right',
    format: (value) => value.toLocaleString('en-US')
  },
  {
    id: 'require',
    label: 'Required quantity',
    minWidth: 170,
    align: 'right',
    format: (value) => value.toLocaleString('en-US')
  },
  {
    id: 'Received',
    label: 'Received quantity',
    minWidth: 170,
    align: 'right',
    format: (value) => value.toLocaleString('en-US')
  },
  {
    id: 'rate',
    label: 'Rate',
    minWidth: 170,
    align: 'right',
    format: (value) => value.toLocaleString('en-US')
  },
  {
    id: 'tax_rate',
    label: 'Tax rate',
    minWidth: 170,
    align: 'right',
    format: (value) => value.toLocaleString('en-US')
  },
  {
    id: 'TotalAmount',
    label: 'Total Amount',
    minWidth: 170,
    align: 'right',
    format: (value) => value.toLocaleString('en-US')
  },
  { id: 'code', label: 'ACTION', minWidth: 100 }
]
const animatedComponents = makeAnimated()
const useStyles = makeStyles((theme) => ({
  root: {
    width: '100%',
    padding:10
  },
  container: {
    maxHeight: 440
  },
  formControl: {
    margin: theme.spacing(1),
    minWidth: 120,
    maxWidth: 300
  },
  chips: {
    display: 'flex',
    flexWrap: 'wrap'
  },
  TimeSpan : {
    fontWeight : 200,
    padding:"10px",
    lightingColor:"#E62E2D"
  },
  chip: {
    margin: 2
  },
  noLabel: {
    marginTop: theme.spacing(3)
  },
  modal: {
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center'
  },
  paper: {
    backgroundColor: theme.palette.background.paper,
    border: '2px solid #000',
    boxShadow: theme.shadows[5],
    padding: theme.spacing(2, 4, 3)
  },
  titleColor :{
    color: "#d52528"
  }
}))

const Agenda = [
          {
            item_code:134,
            item_description:"abc",
            required_qty:2132,
            received_qty:244,
            rate:332,
            tax_rate:122,
            total_amount:21,
            uom:"60f11acacd765e31308b1ded"
        }
        ]

const MasterForm = (props) => {
    // const { register, errors, reset, setValue, handleSubmit, control } = useForm()
    const [ajendaz, setAjenda] = useState()
    const [open, setOpen] = useState(false)
    const [personName, setPersonName] = useState([])
    const [currentTime, setCurrentTime] = useState(new Date().toLocaleTimeString('en-US', { hour12: false }))
    const [stopTime, setStopTime] = useState()
    const classes = useStyles()
    const theme = useTheme()
    const { item, id } = props
    const [location, setLocation] = useState('')
    const [departmentDetail, setDepartment] = useState('')
    const [employeeData, setEmployee] = useState('')
    const [primaryuom, setPrimaryUOM] = useState('')
    const [tooltipOpen, setTooltipOpen] = useState(false)
    const dispatch = useDispatch()

    const { register, errors, reset, setValue, handleSubmit, control } = useForm({defaultValues: props ? props && props.preloadValue && props.preloadValue.detail : ""})
    const [addAjenda, setAddAjenda] = useState([...Agenda])
  const [indexes, setIndexes] = useState([])
  const [counter, setCounter] = useState(0)

  useEffect(() => {
    setValue('location', props && props.preloadValue && props.preloadValue.detail.location._id)
    setValue('department', props && props.preloadValue && props.preloadValue.detail.department._id)
    setValue('employee_name', props && props.preloadValue && props.preloadValue.detail.employee_name._id)
    setValue('required_date', moment(props && props.preloadValue && props.preloadValue.detail.required_date).format('YYYY-MM-DD'))
    setValue("uom", props && props.preloadValue && props.preloadValue.detail.item_detail.uom && props.preloadValue.detail.item_detail.uom._id)
  }, [props && props.preloadValue])

console.log("prloadvalue", props && props.preloadValue && props.preloadValue.detail)
    const addFriend = () => {
    setIndexes(prevIndexes => [...prevIndexes, counter])
    setCounter(prevCounter => prevCounter + 1)
  }

  const removeFriend = index => () => {
    setIndexes(prevIndexes => [...prevIndexes.filter(item => item !== index)])
    setCounter(prevCounter => prevCounter - 1)
  }

// console.log("multipleAjenda", addAjenda)
    const  organization = '/api/master/list_organization'
    useEffect(() => {
      axiosConfig.get(organization)
    .then(r => {
      // console.log(r)
      setLocation(r)
    })
    }, [organization])

  const  department = '/api/master/get_department_list'
    useEffect(() => {
      axiosConfig.get(department)
    .then(r => {
      // console.log(r)
      setDepartment(r)
    })
    }, [department])

      const  employee = '/api/master/list_employee'
    useEffect(() => {
      axiosConfig.get(employee)
    .then(r => {
      // console.log(r)
      setEmployee(r)
    })
    }, [employee])
    

    const  primUOM = '/api/master/list_Unit_of_Measurement'
    useEffect(() => {
    axiosConfig.get(primUOM, {
    headers:{
      token:loginAuthID
    }
    })
    .then(r => {
      // console.log(r)
      setPrimaryUOM(r)
    })
    }, [primUOM])

  const toggle = () => setTooltipOpen(!tooltipOpen)


    // const handleAddAgenda = () => {
    //   setAddAjenda([...Agenda, {addAjenda}])
    // }

    // const handleRemoveAgenda = (I) => {
    //   const value = [...addAjenda]
    //   value.splice(I, 1)
    //   setAddAjenda(value)
    // }

const options = [
  { value: 'chocolate', label: 'Chocolate' },
  { value: 'strawberry', label: 'Strawberry' },
  { value: 'vanilla', label: 'Vanilla' }
]

const handleClickOpen = () => {
  setOpen(true)
}

const handleClose = () => {
  setOpen(false)
}
//entering a list on change
const handleChange = (e) => {
  const ajenda = e.target.value
    setAjenda(ajenda)

}
// handle submit 
const onSubmit = data => {
console.log(data)
  if (props && props.preloadValue && props.preloadValue.detail._id) { 
    dispatch(update_Stationary_Request(data, props))
  } else {
    dispatch(add_Stationary_Request(data, props))
  }
}


    // console.log(props)
return (
  <div className='auth-wrapper auth-v1 px-2'>
  <div className='auth-inner py-2'>         
      <div style={{padding:10}}>
     </div>
          <Form className='auth-login-form mt-2'
          onSubmit={handleSubmit((data, e) => onSubmit(data, e))}  
          onKeyPress={event => {
            if (event.which === 13 /* Enter */) {
              event.preventDefault()
            }
          }}>
      <Card>
        <CardBody>
          <CardTitle className='mb-1' style={{display:"flex"}}>
          <Col md={6}>
             <h5 className={classes.titleColor}>  Stationary Request </h5>
          </Col> 

          </CardTitle>
    <Row  style={{marginTop:"25px"}}>
    <Col md={3}>
          <FormGroup>
        <Label for="exampleDate"> Stationary Request Date</Label>
        <Input
          type="date"
          name="date"
          value={moment(new Date()).format("YYYY-MM-DD")}
          innerRef={register({ required: true, validate: value => value !== '' })}
          id="exampleDate"
          placeholder="date placeholder"
          disabled
        />
      </FormGroup>
      </Col>
        <Col md={3}>
          <FormGroup>
        <Label for="exampleDate"> Stationary Request Number</Label>
        <Input
          type="number"
          name="reqest"
          value={"20"}
            innerRef={register({ required: true, validate: value => value !== '' })}
          id="exampleDate"
          placeholder="Enter your stationary Request Number"
          disabled
        />
      </FormGroup>
      </Col>
            <Col md={3}>
            <FormGroup>
                <Label>
                Location              
                </Label>
                <Input
                 type='select' 
                name="location"
                  className={classnames({ 'is-invalid': errors['location'] })}
                  // onClick={() => props.history.push('/master_of_meeting/googlemap')}
                  innerRef={register({ required: true, validate: value => value !== '' })}
                 placeholder='Enter your location' autoFocus>
                 {location && location.data && location.data.data && location.data.data.map((detail, index) => {
                   return (
                    <option value ={detail._id}>{detail.location_name}</option>
                    )
                  })}
                  </Input>
                 {errors.Location && errors.Location.type === 'required' && <p style={{color:"red"}}>Location id is required</p>}
              </FormGroup>
              </Col>

            <Col md={3}>
            <FormGroup>
                <Label>
                Department              
                </Label>
                <Input type='select' 
                 name="department"
                  className={classnames({ 'is-invalid': errors['department'] })}
                  // onClick={() => props.history.push('/master_of_meeting/googlemap')}
                  innerRef={register({ required: true, validate: value => value !== '' })}
                 placeholder='Enter your Department'>
                     {departmentDetail && departmentDetail.data && departmentDetail.data.data && departmentDetail.data.data.map((detail, index) => {
                   return (
                    <option value ={detail._id}>{detail.department_name}</option>
                    )
                  })}
                  </Input>
                 {errors.department && errors.department.type === 'required' && <p style={{color:"red"}}>department  is required</p>}
              </FormGroup>
              </Col>
            <Col md={3}>
            <FormGroup>
                <Label className='form-label' for='login-email'>
                Employee Name
                </Label>
                <Input type='select' 
                  name="employee_name"
                  className={classnames({ 'is-invalid': errors['employee_name'] })}
                  innerRef={register({ required: true, validate: value => value !== '' })}
                 placeholder='Enter your employee name' autoFocus >
                  {employeeData && employeeData.data && employeeData.data.data && employeeData.data.data.map((detail, index) => {
                   return (
                    <option value ={detail._id}>{detail.employee_type}</option>
                    )
                  })}
                  </Input>
              {errors.employee_name && errors.employee_name.type === 'required' && <p style={{color:"red"}}>employee name id is required</p>}
              </FormGroup>
              </Col>
              <Col md={3}>
      <FormGroup>
        <Label for="exampleDate">Required date</Label>
        <Input
          type="date"
          name="required_date"
          className={classnames({ 'is-invalid': errors['required_date'] })}
           innerRef={register({ required: true, validate: value => value !== '' })}
          placeholder="Date"
        />
      </FormGroup>
      </Col>
</Row>

<div>
<Divider style={{backgroundColor:"#EC8A37"}}/>
</div>
          <Row>     
         {indexes.map(index => {
        const fieldName = `item_detail[${index}]`
        return (
          <fieldset name={fieldName} key={fieldName}>
                  <Col md={12}>
             <h5 className={classes.titleColor}>  Item Detail </h5>
          </Col>
          <Col md={12}>
            <a href="javascript:;">
                 <Icon className="fa fa-trash" id={index} onClick={removeFriend(index)}  style={{ color: red[500], fontSize:"20px", float:"right" }}/>
            </a>
          </Col>

          <Row>
              <Col md={3}>
            <FormGroup>
           <Label>Item Code</Label>
                <Input
                  type="text"
                  name={`${fieldName}.item_code`} 
                   className={classnames({ 'is-invalid': errors['item_code'] })}
                 innerRef={register({ required: true, validate: value => value !== '' })}
                   placeholder='Enter your item code'
              />
                  {errors.item_code && errors.item_code.type === 'required' && <p style={{color:"red"}}>item code is required</p>}
           </FormGroup>
              </Col>

              <Col md={4}>
            <FormGroup>
           <Label>Item description</Label>
                <Input
                  type="textarea"
                  name={`${fieldName}.item_description`}
                      className={classnames({ 'is-invalid': errors['item_description'] })}
                   innerRef={register({ required: true, validate: value => value !== '' })}
                   placeholder='Enter your description'
              />
                  {errors.item_description && errors.item_description.type === 'required' && <p style={{color:"red"}}>Description is required</p>}
           </FormGroup>
              </Col>

              <Col md={3}>
            <FormGroup>
                <Label>
                Required Quantity
                </Label>
                <Input type='text' 
                name={`${fieldName}.required_qty`}
                  className={classnames({ 'is-invalid': errors['required_qty'] })}
                  innerRef={register({ required: true, validate: value => value !== '' })}
                  placeholder='Enter quantity'
                  autoFocus /> 
              {errors.required_qty && errors.required_qty.type === 'required' && <p style={{color:"red"}}>Quantity is required</p>}
              </FormGroup>
              </Col>

              <Col md={2}>
            <FormGroup>
                <Label>
                Received Quantity
                </Label>
                <Input type='text' 
                name={`${fieldName}.received_qty`}
                  className={classnames({ 'is-invalid': errors['received_qty'] })}
                  innerRef={register({ required: true, validate: value => value !== '' })}
                  placeholder='Enter quantity'
                  autoFocus /> 
              {errors.received_qty && errors.received_qty.type === 'required' && <p style={{color:"red"}}>Quantity is required</p>}
              </FormGroup>
              </Col>

              <Col md={3}>
            <FormGroup>
                <Label>
                UOM
                </Label>
                <Input type='select' 
                name={`${fieldName}.uom`}
                  className={classnames({ 'is-invalid': errors['uom'] })}
                  innerRef={register("uom", { required: true, validate: value => value !== '' })}
                  placeholder='Enter uom'
                  autoFocus > 
                  {primaryuom && primaryuom.data && primaryuom.data.data && primaryuom.data.data.map((uom, index) => {
                   return (
                    <option value ={uom._id}>{uom.uom_description}</option>
                    )
                  })}
                  </Input>
              {errors.Tax_rate && errors.Tax_rate.type === 'required' && <p style={{color:"red"}}>Rate is required</p>}
              </FormGroup>
              </Col>

              <Col md={3}>
            <FormGroup>
                <Label>
                Rate
                </Label>
                <Input type='text' 
                name={`${fieldName}.rate`}
                  className={classnames({ 'is-invalid': errors['rate'] })}
                  innerRef={register({ required: true, validate: value => value !== '' })}
                  placeholder='Enter the rate'
                  autoFocus /> 
              {errors.rate && errors.rate.type === 'required' && <p style={{color:"red"}}>Rate is required</p>}
              </FormGroup>
              </Col>

              <Col md={3}>
            <FormGroup>
                <Label>
               Tax Rate
                </Label>
                <Input type='text' 
                name={`${fieldName}.tax_rate`}
                  className={classnames({ 'is-invalid': errors['tax_rate'] })}
                  innerRef={register({ required: true, validate: value => value !== '' })}
                  placeholder='Enter tax rate'
                  autoFocus /> 
              {errors.tax_rate && errors.tax_rate.type === 'required' && <p style={{color:"red"}}>tax rate is required</p>}
              </FormGroup>
              </Col>

          <Col md={3}>
            <FormGroup>
           <Label>Total Amount</Label>
                <Input
                  type="text"
                   name={`${fieldName}.total_amount`}
                   className={classnames({ 'is-invalid': errors['total_amount'] })}
                   innerRef={register({ required: true, validate: value => value !== '' })}
                   placeholder='Enter total amount'
              />
                  {errors.total_amount && errors.total_amount.type === 'required' && <p style={{color:"red"}}>text called is required</p>}
           </FormGroup>
              </Col>
              </Row>
              <div> 
              <Row>

            </Row>
              <Divider style={{backgroundColor:"#EC8A37"}}/>
              </div>
                </fieldset>
               )
             })} 
              <Col md={12}>
              <Button.Ripple color="success"
              // onClick={handleAddAgenda}
              onClick={addFriend}
              size="sm"
               style={{ color: green[500], float:"right", margin:"5px" }}
              >
                Add Item Request
              </Button.Ripple>  
                 {/* <Icon className="fa fa-plus-circle" onClick={handleAddAgenda}  style={{ color: green[500], fontSize:"35px", float:"right" }}/> */}
            </Col>
           <Col md={12}>
              <Button.Ripple className='ripple-button' type="submit" size="sm" style={{float:"right"}} >
                Submit
              </Button.Ripple>  
            </Col>
            </Row>
            </CardBody>
</Card>
            </Form>
            </div>
  </div>
)
}

const StatinaryMaster = (props) => {
  const [addform, setAddForm] = useState(false)
  const [updateuser, setUpdateuser] = useState(false)
  const [addbutton, setAddButton] = useState(false)
  const [userdetail, setUserDetail] = useState([])
  const [user, setUser] = useState([])
  const classes = useStyles()
  const [page, setPage] = useState(0)
  const [rowsPerPage, setRowsPerPage] = useState(10)
  const [searching, setSearchStream] = useState('')
  const handleChangePage = (event, newPage) => {
    setPage(newPage)
  }

  const handleChangeRowsPerPage = (event) => {
    setRowsPerPage(+event.target.value)
    setPage(0)
  }
 ///search table data
const searchChange = (e) => {
 e.preventDefault()
 const searchstream = e.target.value
//  setSearchStream(searchstream)
//  const searchString = searching
if (searchstream.length > 0) {
  const newstring = new RegExp(searchstream, 'gi')
       const searchitem = userdetail && userdetail.data && userdetail.data.data && userdetail.data.data.filter(item => {

         return  item.department !== null ? item.department.department_name.match(newstring) || item.employee_name !== null ? item.employee_name.employee_type.match(newstring)
         || moment(item.required_date).format("YYYY-DD-MM").match(newstring) : '' : ''
          })
       console.log(searchitem)
       setUserDetail({data:{data:searchitem}})
      } else {
        setUserDetail(user)
      }
}
  const onAddClick = () => {
    setAddForm(true)
    setAddButton(true)
    setUpdateuser(false)
  }
  const onCancleClick = () => {
    setAddForm(false)
    setAddButton(false)
  }

  const onEditClick = (detail) => {
    props.history.push({
      state : {
   detail
     }})
     setUpdateuser(true)
     setAddForm(false)
  }
  const userdetailapi = 'http://172.105.49.15:3030/purchase/list_stationary_request'
  useEffect(() => {
    // app.auth().currentUser.getIdToken(false).then(function (Token) {
      axios.get(userdetailapi)
      .then(r => {
        setUser(r)
        setUserDetail(r)
      })
    // })

  }, [addform, updateuser])

  useEffect(() => {
    const {location, history} = props
    //use the state via location.state
    //and replace the state via
    history.replace() 
  }, [history])
console.log(userdetail)
  return (
    <div className='auth-wrapper auth-v1 px-2'>
      <div className='auth-inner py-2'>
      { addform === true ?   <Card className='mb-0'>
          <CardBody>
            <CardTitle tag='h4' className='mb-1'>
            Stationary Request
            </CardTitle>
            {/* <CardText className='mb-2'>Please Register for master</CardText> */}
           <MasterForm onAdd={() => { setAddForm(false) }} />
          </CardBody>
        </Card> : " "}
        {updateuser === true ?   <Card className='mb-0'>
          <CardBody>
            <CardTitle tag='h4' className='mb-1'>
            Stationary Request
            </CardTitle>
            {/* <CardText className='mb-2'>Please Register for master</CardText> */}
            <MasterForm
             preloadValue={props && props.location && props.location.state} onAdd={() => { setUpdateuser(false) }}
             />
          </CardBody>
        </Card> : "" }
      
        <div style={{padding:10}}>
     
        </div>
        <Card className='mb-0'>
          <CardBody> 
          <Row>
              <Col md={3}>
              <h4 style={{margin:'10px'}} className='mb-1'>Stationary Request</h4>
              </Col>
              <Col md={2}>
                  <h6 style={{float:"right", marginTop:"10px"}}>Search</h6>
                  </Col>
              <Col md={3}>
            <FormGroup>
                <Input
                 type='search'
                 onChange = {(e) => searchChange(e)}
                 
                name="image"
                //   className={classnames({ 'is-invalid': errors['image'] })}
                //   innerRef={register({ required: true, validate: value => value !== '' })}
                 placeholder='search...' autoFocus />
              </FormGroup>
              </Col>
              <Col md={4}>
              {addbutton === false ?   <Button.Ripple className='ripple-button' type="submit" size="sm" onClick={onAddClick} style={{margin:'10px', float:"right"}}>
          <i class="fa fa-plus"></i> Add Stationary Request
              </Button.Ripple> : <Button.Ripple className='ripple-button' type="submit" size="sm" onClick={onCancleClick} style={{margin:'10px', float:"right"}}>
          <i class="fa fa-minus"></i> Add Stationary Request
              </Button.Ripple>
              
            }
              </Col>
            </Row>
  
    <TableContainer className={classes.container}>
        <Table stickyHeader size="small" aria-label="a dense table">
          <TableHead>
            <TableRow>
              {columns.map((column) => (
                <TableCell
                  key={column.id}
                  align={column.align}
                  style={{ minWidth: column.minWidth }}
                >
                  {column.label}
                </TableCell>
              ))}
            </TableRow>
          </TableHead>
          <TableBody>
            {userdetail && userdetail.data && userdetail.data.data && userdetail.data.data.slice((page * rowsPerPage), (page * rowsPerPage) + rowsPerPage).map((detail, index) => {
              // console.log(detail)
              return (
                <TableRow hover role="checkbox" tabIndex={-1}>
                      <TableCell>
                    {index + 1}
                      </TableCell>
                      <TableCell>
                      {detail.location === null ? 'Not found' : detail.location.location_name}
                      </TableCell>
                      <TableCell>
                      {detail.department === null ? 'Not found' : detail.department.department_name}
                      </TableCell>
                      <TableCell>
                      {detail.employee_name === null ? 'Not found' : detail.employee_name.employee_type}
                      </TableCell>
                      <TableCell>
                      {moment(detail.required_date).format("DD-MM-YYYY")}
                      </TableCell>
                      <TableCell>
                      {detail.item_detail ? detail.item_detail.map((item) => <span>{item.item_code},</span>) : ''}
                      </TableCell>
                      <TableCell>
                      {detail.item_detail ? detail.item_detail.map((item) => { return <span>{ item.uom === null ? 'Not found' : item.uom.uom_description},</span> }) : ''}
                      </TableCell>
                      <TableCell>
                       {detail.item_detail ? detail.item_detail.map((item) => <span>{item.item_description},</span>) : ''}
                      </TableCell>
                      <TableCell>
                      {detail.item_detail ? detail.item_detail.map((item) => <span>{item.required_qty},</span>) : ''}
                      </TableCell>
                      <TableCell>
                      {detail.item_detail ? detail.item_detail.map((item) => <span>{item.received_qty},</span>) : ''}
                      </TableCell>
                      <TableCell>
                      {detail.item_detail ? detail.item_detail.map((item) => <span>{item.rate},</span>) : ''}
                      </TableCell>
                      <TableCell>
                      {detail.item_detail ? detail.item_detail.map((item) => <span>{item.tax_rate},</span>) : ''}
                      </TableCell>
                      <TableCell>
                      {detail.item_detail ? detail.item_detail.map((item) => <span>{item.total_amount},</span>) : ''}
                      </TableCell>
                      {/* <TableCell>
                      {detail.status === true ? <Switch
        checked={true}
        color="primary"
        name="checkedB"
        inputProps={{ 'aria-label': 'primary checkbox' }}
      /> : <Switch
      checked={false}
      name="checkedA"
      inputProps={{ 'aria-label': 'secondary checkbox' }}
    />}
                      </TableCell> */}
                      <TableCell>
                      <Button.Ripple className='btn-icon btn-sm rounded-circle' color='flat-success'  onClick={() => onEditClick(detail, props)} outline id={`positionTop_${index + 1}`}>
        <Edit2 size={16} />
      </Button.Ripple>
      <UncontrolledTooltip placement='top' target={`positionTop_${index + 1}`}>
       Edit
      </UncontrolledTooltip>
                      </TableCell>
                </TableRow>
              )
            })}
          </TableBody>
        </Table>
      </TableContainer>
      <TablePagination
        rowsPerPageOptions={[10, 20, 30]}
        component="div"
        count={userdetail && userdetail.data && userdetail.data.data && userdetail.data.data.length}
        rowsPerPage={rowsPerPage}
        page={page}
        onPageChange={handleChangePage}
        onRowsPerPageChange={handleChangeRowsPerPage}
      />

    </CardBody>
    </Card>
      </div>
    </div>
  )
}

export default StatinaryMaster
