// ** React Imports
import { Fragment, useState } from 'react'

// ** Mail Components Imports
import MailCard from './MailCard'
import MailDetails from './MailDetails'
import ComposePopUp from './ComposePopup'

// ** Utils
import { formatDateToMonthShort } from '@utils'

import InputAdornment from "@material-ui/core/InputAdornment"
import TextField from "@material-ui/core/TextField"
import SearchIcon from "@material-ui/icons/Search"
import { IconButton } from "@material-ui/core"
import CancelRoundedIcon from "@material-ui/icons/CancelRounded"

// ** Third Party Components
import PerfectScrollbar from 'react-perfect-scrollbar'
import {
  InputGroup,
  InputGroupAddon,
  Input,
  InputGroupText,
  CustomInput,
  DropdownMenu,
  DropdownItem,
  DropdownToggle,
  UncontrolledDropdown,
  Row,
  Col
} from 'reactstrap'
import { Menu, Search, ArrowLeftCircle, Folder, Tag, Mail, Trash, Edit2, Info } from 'react-feather'

const Mails = props => {
  // ** Props
  const {
    query,
    store,
    setQuery,
    dispatch,
    selectMail,
    composeOpen,
    updateMails,
    paginateMail,
    selectAllMail,
    toggleCompose,
    setSidebarOpen,
    updateMailLabel,
    resetSelectedMail,
    selectCurrentMail
  } = props

  const { mails, selectedMails } = store

  // ** States
  const [openMail, setOpenMail] = useState(false)

  // ** Variables
  const labelColors = {
    personal: 'success',
    company: 'primary',
    important: 'warning',
    private: 'danger'
  }

  // ** Handles Update Functions
  const handleMailClick = id => {
    dispatch(selectCurrentMail(id))
    setOpenMail(true)
  }

  /*eslint-disable */

  // ** Handles Folder Update
  const handleFolderUpdate = (e, folder, ids = selectedMails) => {
    e.preventDefault()
    dispatch(updateMails(ids, { folder }))
    dispatch(resetSelectedMail())
  }

  // ** Handles Label Update
  const handleLabelsUpdate = (e, label, ids = selectedMails) => {
    e.preventDefault()
    dispatch(updateMailLabel(ids, label))
    dispatch(resetSelectedMail())
  }

  // ** Handles Mail Read Update
  const handleMailReadUpdate = (arr, bool) => {
    dispatch(updateMails(arr, { isRead: bool })).then(() => dispatch(resetSelectedMail()))
    dispatch(selectAllMail(false))
  }

  // ** Handles Move to Trash
  const handleMailToTrash = ids => {
    dispatch(updateMails(ids, { folder: 'trash' }))
    dispatch(resetSelectedMail())
  }
  /*eslint-enable */

  // ** Renders Mail
  const renderMails = () => {
    if (mails.length) {
      return mails.map((mail, index) => {
        return (
          <MailCard
            mail={mail}
            key={index}
            dispatch={dispatch}
            selectMail={selectMail}
            updateMails={updateMails}
            labelColors={labelColors}
            selectedMails={selectedMails}
            handleMailClick={handleMailClick}
            handleMailReadUpdate={handleMailReadUpdate}
            formatDateToMonthShort={formatDateToMonthShort}
          />
        )
      })
    }
  }

  return (
    <Fragment>
      {/* <Row> */}
        {/* <Col md={6}> */}
      <div className='email-app-list'>
        <div className='app-fixed-search d-flex align-items-center'>
          <div className='sidebar-toggle d-block d-lg-none ml-1' onClick={() => setSidebarOpen(true)}>
            <ArrowLeftCircle /> 
          </div>
          <div className='d-flex align-content-center justify-content-between w-100'>
            {/* <InputGroup className='input-group-merge'>
              <InputGroupAddon addonType='prepend'>
                <InputGroupText>
                  <Search className='text-muted' size={14} />
                </InputGroupText>
              </InputGroupAddon>
              <Input
                id='email-search'
                placeholder='Search email'
                value={query}
                onChange={e => setQuery(e.target.value)}
              /> */}
            {/* </InputGroup> */}
            <TextField
          placeholder="Search"
          type="text"
          variant="outlined"
          fullWidth
          size="small"
          // onChange={(e) => setValue(e.target.value)}
          // value={value}
          InputProps={{
                  startAdornment: (
                    <InputAdornment position="start">
                      <SearchIcon />
                    </InputAdornment>
                  ),
                  endAdornment: value && (
                  <IconButton
                    aria-label="toggle password visibility"
                    // onClick={() => [setSearchVisible(!search), setValue("")] }
                  >
                    <CancelRoundedIcon />
                  </IconButton>
                )
              }}
            />

          </div>
        </div>


        <PerfectScrollbar className='email-user-list' options={{ wheelPropagation: false }}>
        <div className='app-action light-gray-bg'>
          <div className='action-left' style={{width:"100%"}}>
          <p className='text-truncate mb-0'>Tread name display for longer text eclips Tread name display for longer text eclipse Tread name display for longer text eclipse Tread name display for longer text eclipseTread name display for longer text eclipseTread name display for longer text eclipseTread name display for longer text eclipseTread name display for longer text eclipseTread name display for longer text eclipseTread name display for longer text eclipseTread name display for longer text eclipseTread name display for longer text eclipse</p>
          </div>
            <div className='action-right'>
            <Info size={18} />
            </div>
        </div>

          {mails.length ? (
            <ul className='email-media-list'>{renderMails()}</ul>
          ) : (
            <div className='no-results d-block'>
              <h5>No Items Found</h5>
            </div>
        )} 
        </PerfectScrollbar>
      </div>
      {/* </Col>
      <Col  md={6}>
        sadadadj */}
      <MailDetails
        openMail={openMail}
        dispatch={dispatch}
        mail={store.currentMail}
        labelColors={labelColors}
        setOpenMail={setOpenMail}
        updateMails={updateMails}
        paginateMail={paginateMail}
        updateMailLabel={updateMailLabel}
        handleMailToTrash={handleMailToTrash}
        handleFolderUpdate={handleFolderUpdate}
        handleLabelsUpdate={handleLabelsUpdate}
        handleMailReadUpdate={handleMailReadUpdate}
        formatDateToMonthShort={formatDateToMonthShort}
      />
      <ComposePopUp composeOpen={composeOpen} toggleCompose={toggleCompose} />
      {/* </Col>
      </Row> */}
    </Fragment>
  )
}

export default Mails
