import {React, useState, useContext, Fragment, useEffect } from 'react'
import {Col, Row, Card, CardBody, CardTitle, CardText, Form, FormGroup, Label, Input, InputGroup, InputGroupAddon, InputGroupText, CustomInput, Button } from 'reactstrap'
const moment = require('moment')
import { useForm, Controller } from 'react-hook-form'
import classnames from 'classnames'
import { isObjEmpty, selectThemeColors } from '@utils'
import { ArrowLeft, ArrowRight, Plus} from 'react-feather'
import axios from 'axios'
import Select from 'react-select'
import Flatpickr from 'react-flatpickr'
import "flatpickr/dist/themes/material_green.css"
import Icon from '@material-ui/core/Icon'


const Details_of_changes = ({ stepper, type }) => {
  // const { register, errors, handleSubmit, trigger } = useForm()
  const { register, errors, control, setValue, reset, handleSubmit } = useForm()


  const onSubmit = (data, e) => {
    e.preventDefault()
    console.log(data)
    
    // trigger()
    // if (isObjEmpty(errors)) {
      stepper.next()
    // }
  }

  
  const [inputList, setInputList] = useState([{  impact_department:"", comment_impact:"", agree:"", sign:"", date:""  }])

  // handle input change
  const handleInputChange = (e, index) => {
    const { name, value } = e.target
    const list = [...inputList]
    list[index][name] = value
    setInputList(list)
  }
 
  // handle click event of the Remove button
  const handleRemoveClick = index => {
    const list = [...inputList]
    list.splice(index, 1)
    setInputList(list)
  }
 
  // handle click event of the Add button
  const handleAddClick = () => {
    setInputList([...inputList, { impact_department:"", comment_impact:"", agree:"", sign:"", date:"" }])
  }
 
  return (
    <Fragment>
      {/* <div className='content-header'>
        <h5 className='mb-0'>Details Of Changes </h5>
        <small>Enter Details Of Changes.</small>
      </div> */}
      <Form  onSubmit={handleSubmit((data, e) => onSubmit(data, e))}>

      {inputList.map((x, i) => {
        return (
          <div className="row">
       <Col md={3}>
    <FormGroup>
          <Label className='form-label' for='impact_department'>Department </Label>
          <Input type='text'  name="impact_department" value={x.impact_department}   onChange={e => handleInputChange(e, i)} > </Input>
      </FormGroup>
  </Col>
            <Col md={3}>
          <FormGroup>
              <Label className='form-label' for='comment_impact'>Comment About Impact </Label>
              <Input type='text'  name="comment_impact" value={x.comment_impact}   onChange={e => handleInputChange(e, i)} > </Input>
          </FormGroup>
          </Col>

          <Col md={1}>
          <FormGroup>
              <Label className='form-label' for='agree'>Agree</Label>
              <Input type='select'  name="agree" value={x.agree}   onChange={e => handleInputChange(e, i)} >
              <option value="Yes">Yes</option>
              <option value="No">No</option>
              </Input>
              
          </FormGroup>
          </Col>

          <Col md={1}>
          <FormGroup>
              <Label className='form-label' for='agree'>Sign</Label>
              <Input type='text'  name="sign" value={x.sign}   onChange={e => handleInputChange(e, i)} >
              </Input>
              
          </FormGroup>
          </Col>
          <Col md={2}>
          <FormGroup>
              <Label className='form-label' for='date'>Date</Label>
              <Input type='text'  name="date" value={x.date}   onChange={e => handleInputChange(e, i)} >
              </Input>
          </FormGroup>
          </Col>

            {/* <input
              className="ml10"
              name="lastName"
   placeholder="Enter Last Name"
              value={x.lastName}
              onChange={e => handleInputChange(e, i)}
            /> */}
            {/* <div className="btn-box"> */}
            <Col md={2} style={{marginTop:"16px"}}>
              {inputList.length !== 1 && 
            //   <Button.Ripple className='waves-effect ripple-button btn btn-sm btn-gradient-danger' >
            //   <Trash color='danger' onClick={() => handleRemoveClick(i)} size={14} />
            // </Button.Ripple>
           <a href="javascript:;"> <Icon className="fa fa-trash" id={i} onClick={() => handleRemoveClick(i)}  style={{color:"#f92626bf", marginTop:"15px", fontSize:"20px"}}/></a>
               }

              {inputList.length - 1 === i &&  <Button.Ripple className='waves-effect ripple-button btn btn-primary btn-sm'  onClick={handleAddClick}>
          <Plus size={14} />
          <span className='align-middle ml-25'>Add</span>
        </Button.Ripple>  
        }
        </Col>
            </div>
        )
      })}

       <Row>
        <Col md={3}>
            <FormGroup>
                <Label className='form-label' for='change_management_committee'>Management Committee<span  style={{color:"red"}} className="required">*</span></Label>
                <Input type='select'  name="change_management_committee"  id="change_management_committee"
                  className={classnames({ 'is-invalid': errors['change_management_committee'] })}
                  innerRef={register({ required: true, validate: value => value !== '' })}
                 placeholder='Enter ' autoFocus >
                   <option value="">Select Option</option>
                   <option value="Accept">Accept</option>
                   <option value="Reject">Reject</option>
                 </Input>
             {errors.change_management_committee && errors.change_management_committee.type === 'required' && <span style={{color:"red"}}>Please enter </span>}
              </FormGroup>
              </Col>
              <Col md={3}>
                  <FormGroup>
                      <Label className='form-label' for='finalized_date_of_implemention'> Finalized Date Of Implementation <span  style={{color:"red"}} className="required">*</span></Label>
                  <Controller
                as={
                <Flatpickr name="finalized_date_of_implemention" />}
                    className='form-control'
                    name="finalized_date_of_implemention"
                    control={control}
                    placeholder='DD-MM-YYYY '
                    ref={register("finalized_date_of_implemention", { required: true })}
                    options={{ dateFormat: "d-m-Y" }}
                    />
                {errors.finalized_date_of_implemention && errors.finalized_date_of_implemention.type === 'required' && <span style={{color:"red"}}>Please select target date </span>}
                
                    </FormGroup>
                    </Col>
              </Row>
        <div className='d-flex justify-content-between'>
          <Button.Ripple color='primary' className='btn-prev' onClick={() => stepper.previous()}>
            <ArrowLeft size={14} className='align-middle mr-sm-25 mr-0'></ArrowLeft>
            <span className='align-middle d-sm-inline-block d-none'>Previous</span>
          </Button.Ripple>
          <Button.Ripple type='submit' color='primary' className='btn-next'>
            <span className='align-middle d-sm-inline-block d-none'>Next</span>
            <ArrowRight size={14} className='align-middle ml-sm-25 ml-0'></ArrowRight>
          </Button.Ripple>
        </div>
      </Form>
    </Fragment>
  )
}

export default Details_of_changes
