import { useRef, useState } from 'react'
import Wizard from '@components/wizard'
import { ArrowRight } from 'react-feather'
import Implementation_readiness from './form/Implementation_readiness'
import Activity from './form/Activity'
import Disposal_recommendation from './form/Disposal_recommendation'
import Details_changes from './form/Details_of_changes'
import ECRDetails from './form/ECRDetails'
import AboutImpact from './form/About_Impact'

const WizardHorizontal = () => {
  const [stepper, setStepper] = useState(null)
  const ref = useRef(null)

  const steps = [
    {
      id: 'ECR-details',
      title: 'ECR Details',
      subtitle: 'Enter Your ECR Details.',
      content: <ECRDetails stepper={stepper} type='wizard-horizontal' />
    },
    {
      id: 'details-of-changes',
      title: 'Details of Changes',
      subtitle: 'Add details of changes',
      content: <Details_changes stepper={stepper} type='wizard-horizontal' />
    },
    {
      id: 'about_impact',
      title: 'About Impact',
      subtitle: 'Add About Impact',
      content: <AboutImpact stepper={stepper} type='wizard-horizontal' />
    },
    {
      id: 'Disposal_recommendation',
      title: 'Disposal Recommendation',
      subtitle: 'Add Disposal Recommendation',
      content: <Disposal_recommendation stepper={stepper} type='wizard-horizontal' />
    },

    {
      id: 'Implementation_readiness',
      title: 'Implementation Readiness',
      subtitle: 'Add Implementation Readiness',
      content: <Implementation_readiness stepper={stepper} type='wizard-horizontal' />
    },
    {
      id: 'Activity',
      title: 'Activity',
      subtitle: 'Add Activity',
      content: <Activity stepper={stepper} type='wizard-horizontal' />
    }
  ]

  return (
    <div className='horizontal-wizard'>
      <Wizard instance={el => setStepper(el)} ref={ref} steps={steps} />
    </div>
  )
}

export default WizardHorizontal
