import {React, useState, useContext, Fragment, useEffect } from 'react'
import { Facebook, Twitter, Mail, GitHub, Edit2 } from 'react-feather'
import {Col, Row, Card, CardBody, CardTitle, CardText, Form, FormGroup, UncontrolledTooltip, Label, Input, Tooltip, InputGroup, InputGroupAddon, ListGroup, ListGroupItem, InputGroupText, CustomInput, Button } from 'reactstrap'
const moment = require('moment')
import { useForm, Controller } from 'react-hook-form'
import classnames from 'classnames'
import { useDispatch } from 'react-redux'
import { handleMaster, handleMasterUpdate } from '@store/actions/masteruser'
import axios from 'axios'
import '@styles/base/pages/page-auth.scss'
import { makeStyles, useTheme  } from '@material-ui/core/styles'
import useMediaQuery from '@material-ui/core/useMediaQuery'
import 'bootstrap/dist/css/bootstrap.min.css'
// import InputAdornment from "@material-ui/core/InputAdornment"
import makeAnimated from 'react-select/animated'
import Divider from '@material-ui/core/Divider'
import Icon from '@material-ui/core/Icon'
import { green, red } from '@material-ui/core/colors'
import Switch from '@material-ui/core/Switch'
import Table from '@material-ui/core/Table'
import TableBody from '@material-ui/core/TableBody'
import TableCell from '@material-ui/core/TableCell'
import TableContainer from '@material-ui/core/TableContainer'
import TableHead from '@material-ui/core/TableHead'
import TablePagination from '@material-ui/core/TablePagination'
import TableRow from '@material-ui/core/TableRow'
import axiosConfig from './../../../axiosConfig'

const loginAuthID = sessionStorage.getItem('id_token')
const columns = [
  { id: 'index', label: 'Sr. No.', minWidth: 100 },
  {
    id: 'StationaryRequest',
    label: 'Stationary Request Date',
    minWidth: 170,
    align: 'right',
    format: (value) => value.toLocaleString('en-US')
  },
  {
    id: 'StationaryRequest',
    label: 'Stationary Request Number',
    minWidth: 170,
    align: 'right',
    format: (value) => value.toLocaleString('en-US')
  },
  {
    id: 'Location',
    label: 'Location',
    minWidth: 170,
    align: 'right',
    format: (value) => value.toLocaleString('en-US')
  },
  {
    id: 'Department',
    label: 'Department',
    minWidth: 170,
    align: 'right',
    format: (value) => value.toLocaleString('en-US')
  },
  {
    id: 'EmployeeName',
    label: 'Employee Name',
    minWidth: 170,
    align: 'right',
    format: (value) => value.toLocaleString('en-US')
  },
  {
    id: 'required_date',
    label: 'Required date',
    minWidth: 170,
    align: 'right',
    format: (value) => value.toLocaleString('en-US')
  },
  {
    id: 'item_code',
    label: 'item code',
    minWidth: 170,
    align: 'right',
    format: (value) => value.toLocaleString('en-US')
  },
  {
    id: 'item_Discription',
    label: 'Item Discription',
    minWidth: 170,
    align: 'right',
    format: (value) => value.toLocaleString('en-US')
  },
  {
    id: 'uom',
    label: 'uom',
    minWidth: 170,
    align: 'right',
    format: (value) => value.toLocaleString('en-US')
  },
  {
    id: 'require',
    label: 'Required quantity',
    minWidth: 170,
    align: 'right',
    format: (value) => value.toLocaleString('en-US')
  },
  {
    id: 'tax_rate',
    label: 'Received quantity',
    minWidth: 170,
    align: 'right',
    format: (value) => value.toLocaleString('en-US')
  },
  {
    id: 'rate',
    label: 'Rate',
    minWidth: 170,
    align: 'right',
    format: (value) => value.toLocaleString('en-US')
  },
  {
    id: 'tax_rate',
    label: 'Tax rate',
    minWidth: 170,
    align: 'right',
    format: (value) => value.toLocaleString('en-US')
  },
  {
    id: 'TotalAmount',
    label: 'Total Amount',
    minWidth: 170,
    align: 'right',
    format: (value) => value.toLocaleString('en-US')
  },
  { id: 'code', label: 'ACTION', minWidth: 100 }
]
const animatedComponents = makeAnimated()
const useStyles = makeStyles((theme) => ({
  root: {
    width: '100%',
    padding:10
  },
  container: {
    maxHeight: 440
  },
  formControl: {
    margin: theme.spacing(1),
    minWidth: 120,
    maxWidth: 300
  },
  chips: {
    display: 'flex',
    flexWrap: 'wrap'
  },
  TimeSpan : {
    fontWeight : 200,
    padding:"10px",
    lightingColor:"#E62E2D"
  },
  chip: {
    margin: 2
  },
  noLabel: {
    marginTop: theme.spacing(3)
  },
  modal: {
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center'
  },
  paper: {
    backgroundColor: theme.palette.background.paper,
    border: '2px solid #000',
    boxShadow: theme.shadows[5],
    padding: theme.spacing(2, 4, 3)
  },
  titleColor :{
    color: "#d52528"
  }
}))

const Agenda = [{}]

const MasterForm = (props) => {
    // const { register, errors, reset, setValue, handleSubmit, control } = useForm()
    const [ajendaz, setAjenda] = useState()
    const [open, setOpen] = useState(false)
    const [personName, setPersonName] = useState([])
    const [currentTime, setCurrentTime] = useState(new Date().toLocaleTimeString('en-US', { hour12: false }))
    const [stopTime, setStopTime] = useState()
    const classes = useStyles()
    const theme = useTheme()
    const { item, id } = props
    const [tooltipOpen, setTooltipOpen] = useState(false)
    const dispatch = useDispatch()

    const { register, errors, reset, setValue, handleSubmit, control } = useForm({defaultValues: props ? props && props.preloadValue && props.preloadValue.detail : ""})
    const [addAjenda, setAddAjenda] = useState([...Agenda])
    
  console.log(currentTime)
    
  const toggle = () => setTooltipOpen(!tooltipOpen)
    const handleAddAgenda = () => {
      console.log(Agenda, {})
      setAddAjenda([...Agenda, {}])
    }

    const handleRemoveAgenda = (I) => {
      // console.log(e.target.id)
      // const removeAgenda = Agenda.filter((item, index) => {
      //   console.log(Agenda, index)
      //   return Agenda !== e.target.id
      // })
      const value = [...addAjenda]
      value.splice(I, 1)
      setAddAjenda(value)
      // console.log(removeAgenda)
      // setAddAjenda({Agenda : [Agenda.filter()]})
    }
// console.log(errors)

const options = [
  { value: 'chocolate', label: 'Chocolate' },
  { value: 'strawberry', label: 'Strawberry' },
  { value: 'vanilla', label: 'Vanilla' }
]

const handleClickOpen = () => {
  setOpen(true)
}

const handleClose = () => {
  setOpen(false)
}
//entering a list on change
const handleChange = (e) => {
  // console.log(e.target.value)
  // if (e.key === 'Enter') {
  
  const ajenda = e.target.value
    setAjenda(ajenda)
    //  e.preventDefault()
  // personName.push(ajenda)
  // setValue("ajenda", personName)
  // setPersonName(personName)
//   reset()
// }
}

//multiselect attendies value
const changeValue = (selected) => {
  // console.log(stuff)
// console.log(selected)
setValue("attendies", selected)
}

//multiselect other attendies
const changeOtherAttendies = (selected) => {
  // console.log(stuff)
// console.log(selected)
setValue("other_attendies", selected)
}

//delete the list
const onDelete = (e) => {
  const ajendas = personName.filter((item, index) => {
    // console.log(index)
    return index !== e
  })
  // console.log(ajendas)
  setPersonName(ajendas)
}


// handle submit 
const onSubmit = data => {
  // alert("SUBMITED SUCCESSFULLY")
  // if (props && props.preloadValue && props.preloadValue.detail._id) { 
  //   dispatch(update_Purchase_Request(data, props))
  // } else {
  //   dispatch(add_Purchase_Request(data, props))
  // }
}


    // console.log(props)
return (
  <div className='auth-wrapper auth-v1 px-2'>
  <div className='auth-inner py-2'>         
      <div style={{padding:10}}>
     </div>
          <Form className='auth-login-form mt-2'
          onSubmit={handleSubmit((data, e) => onSubmit(data, e))}  
          onKeyPress={event => {
            if (event.which === 13 /* Enter */) {
              event.preventDefault()
            }
          }}>
      <Card>
        <CardBody>
          <CardTitle className='mb-1' style={{display:"flex"}}>
          <Col md={6}>
             <h5 className={classes.titleColor}>  Stationary Request </h5>
          </Col> 

          </CardTitle>
    <Row  style={{marginTop:"25px"}}>
    <Col md={3}>
          <FormGroup>
        <Label for="exampleDate"> Stationary Request Date</Label>
        <Input
          type="date"
          name="date"
          id="exampleDate"
          placeholder="date placeholder"
        />
      </FormGroup>
      </Col>
        <Col md={3}>
          <FormGroup>
        <Label for="exampleDate"> Stationary Request Number</Label>
        <Input
          type="number"
          name="reqest"
          id="exampleDate"
          placeholder="Enter your stationary Request Number"
        />
      </FormGroup>
      </Col>
            <Col md={3}>
            <FormGroup>
                <Label>
                Location              
                </Label>
                <Input type='select' 
                name="Location"
                  className={classnames({ 'is-invalid': errors['Location'] })}
                  // onClick={() => props.history.push('/master_of_meeting/googlemap')}
                  innerRef={register({ required: true, validate: value => value !== '' })}
                 placeholder='Enter your Location' autoFocus>
                 <option>Pune</option>
                 <option>Aumba</option>
                 <option>Mumbai</option>
                 <option>JW MEET</option>
                 </Input>
                 {errors.Location && errors.Location.type === 'required' && <p style={{color:"red"}}>Location id is required</p>}
              </FormGroup>
              </Col>

            <Col md={3}>
            <FormGroup>
                <Label>
                Department              
                </Label>
                <Input type='text' 
                name="Unit"
                  className={classnames({ 'is-invalid': errors['Unit'] })}
                  // onClick={() => props.history.push('/master_of_meeting/googlemap')}
                  innerRef={register({ required: true, validate: value => value !== '' })}
                 placeholder='Enter your Department' autoFocus/>
                 {errors.Unit && errors.Unit.type === 'required' && <p style={{color:"red"}}>Unit  is required</p>}
              </FormGroup>
              </Col>
            <Col md={3}>
            <FormGroup>
                <Label className='form-label' for='login-email'>
                Employee Name
                </Label>
                <Input type='text' 
                name="Venue"
                  className={classnames({ 'is-invalid': errors['Venue'] })}
                  innerRef={register({ required: true, validate: value => value !== '' })}
                 placeholder='Enter your employee name' autoFocus />
              {errors.Venue && errors.Venue.type === 'required' && <p style={{color:"red"}}>Venue id is required</p>}
              </FormGroup>
              </Col>
              <Col md={3}>
      <FormGroup>
        <Label for="exampleTime">Required date</Label>
        <Input
          type="date"
          name="date"
          id="exampleDate"
          placeholder="time placeholder"
        />
      </FormGroup>
      </Col>
</Row>

<div>
<Divider style={{backgroundColor:"#EC8A37"}}/>
</div>
          <Row>     
             {addAjenda.map((agenda, index) => {
               return (
                <div>
                  <Col md={12}>
             <h5 className={classes.titleColor}>  Item Detail </h5>
          </Col>
          <Col md={12}>
            <a href="javascript:;">
                 <Icon className="fa fa-trash" id={index} onClick={() => handleRemoveAgenda(index)}  style={{ color: red[500], fontSize:"20px", float:"right" }}/>
            </a>
          </Col>

          <Row>
              <Col md={3}>
            <FormGroup>
           <Label>Item Code</Label>
                <Input
                  type="text"
                  value={agenda.label}
                  innerRef={register({ required: true, validate: value => value !== '' })}
                   name="text"
                   placeholder='Enter your item code'
              />
                  {errors.text && errors.text.type === 'required' && <p style={{color:"red"}}>item code is required</p>}
           </FormGroup>
              </Col>

              <Col md={4}>
            <FormGroup>
           <Label>Item description</Label>
                <Input
                  type="textarea"
                  innerRef={register({ required: true, validate: value => value !== '' })}
                   name="action"
                   placeholder='Enter your description'
              />
                  {errors.text && errors.text.type === 'required' && <p style={{color:"red"}}>Description is required</p>}
           </FormGroup>
              </Col>

              <Col md={3}>
            <FormGroup>
                <Label>
                Required Quantity
                </Label>
                <Input type='text' id='code'
                name="Tax_rate"
                  className={classnames({ 'is-invalid': errors['Tax_rate'] })}
                  innerRef={register({ required: true, validate: value => value !== '' })}
                  placeholder='Enter quantity'
                  autoFocus /> 
              {errors.Tax_rate && errors.Tax_rate.type === 'required' && <p style={{color:"red"}}>Rate is required</p>}
              </FormGroup>
              </Col>

              <Col md={2}>
            <FormGroup>
                <Label>
                Received Quantity
                </Label>
                <Input type='text' id='code'
                name="Tax_rate"
                  className={classnames({ 'is-invalid': errors['Tax_rate'] })}
                  innerRef={register({ required: true, validate: value => value !== '' })}
                  placeholder='Enter quantity'
                  autoFocus /> 
              {errors.Tax_rate && errors.Tax_rate.type === 'required' && <p style={{color:"red"}}>Rate is required</p>}
              </FormGroup>
              </Col>

              <Col md={3}>
            <FormGroup>
                <Label>
                UOM
                </Label>
                <Input type='select' id='code'
                name="Tax_rate"
                  className={classnames({ 'is-invalid': errors['Tax_rate'] })}
                  innerRef={register({ required: true, validate: value => value !== '' })}
                  placeholder='Enter uom'
                  autoFocus /> 
              {errors.Tax_rate && errors.Tax_rate.type === 'required' && <p style={{color:"red"}}>Rate is required</p>}
              </FormGroup>
              </Col>

              <Col md={3}>
            <FormGroup>
                <Label>
                Rate
                </Label>
                <Input type='text' id='code'
                name="Tax_rate"
                  className={classnames({ 'is-invalid': errors['Tax_rate'] })}
                  innerRef={register({ required: true, validate: value => value !== '' })}
                  placeholder='Enter the rate'
                  autoFocus /> 
              {errors.Tax_rate && errors.Tax_rate.type === 'required' && <p style={{color:"red"}}>Rate is required</p>}
              </FormGroup>
              </Col>

              <Col md={3}>
            <FormGroup>
                <Label>
               Tax Rate
                </Label>
                <Input type='text' id='code'
                name="Tax_rate"
                  className={classnames({ 'is-invalid': errors['Tax_rate'] })}
                  innerRef={register({ required: true, validate: value => value !== '' })}
                  placeholder='Enter tax rate'
                  autoFocus /> 
              {errors.Tax_rate && errors.Tax_rate.type === 'required' && <p style={{color:"red"}}>Rate is required</p>}
              </FormGroup>
              </Col>

          <Col md={3}>
            <FormGroup>
           <Label>Total Amount</Label>
                <Input
                  type="text"
                  innerRef={register({ required: true, validate: value => value !== '' })}
                   name="Total_amount"
                   placeholder='Enter total amount'
              />
                  {errors.text && errors.text.type === 'required' && <p style={{color:"red"}}>text called is required</p>}
           </FormGroup>
              </Col>
              </Row>
              <div> 
              <Row>
              <Col md={12}>
              <Button.Ripple color="success"
              onClick={handleAddAgenda}
              size="sm"
               style={{ color: green[500], float:"right", margin:"5px" }}
              >
                Add New Row
              </Button.Ripple>  
                 {/* <Icon className="fa fa-plus-circle" onClick={handleAddAgenda}  style={{ color: green[500], fontSize:"35px", float:"right" }}/> */}
            </Col>
            </Row>
              <Divider style={{backgroundColor:"#EC8A37"}}/>
              </div>
                </div>
               )
             })} 

           <Col md={12}>
              <Button.Ripple className='ripple-button' type="submit" size="sm" style={{float:"right"}} >
                Submit
              </Button.Ripple>  
            </Col>
            </Row>
            </CardBody>
</Card>
            </Form>
            </div>
  </div>
)
}

const StatinaryMaster = (props) => {
  const [addform, setAddForm] = useState(false)
  const [updateuser, setUpdateuser] = useState(false)
  const [addbutton, setAddButton] = useState(false)
  const [userdetail, setUserDetail] = useState([])
  const [user, setUser] = useState([])
  const classes = useStyles()
  const [page, setPage] = useState(0)
  const [rowsPerPage, setRowsPerPage] = useState(10)
  const [searching, setSearchStream] = useState('')
  const handleChangePage = (event, newPage) => {
    setPage(newPage)
  }

  const handleChangeRowsPerPage = (event) => {
    setRowsPerPage(+event.target.value)
    setPage(0)
  }
 ///search table data
const searchChange = (e) => {
 e.preventDefault()
 const searchstream = e.target.value
//  setSearchStream(searchstream)
//  const searchString = searching
if (searchstream.length > 3) {
  const newstring = new RegExp(searchstream, 'gi')
       const searchitem = userdetail && userdetail.data && userdetail.data.data && userdetail.data.data.filter(item => {

         return item.vendor_name.match(newstring) 
       })
       console.log(searchitem)
       setUserDetail({data:{data:searchitem}})
      } else {
        setUserDetail(user)
      }
}
  const onAddClick = () => {
    setAddForm(true)
    setAddButton(true)
    setUpdateuser(false)
  }
  const onCancleClick = () => {
    setAddForm(false)
    setAddButton(false)
  }

  const onEditClick = (detail) => {
    props.history.push({
      state : {
   detail
     }})
     setUpdateuser(true)
     setAddForm(false)
  }
  const userdetailapi = 'http://172.105.49.15:3030/purchase/list_purchase_master'
  useEffect(() => {
    // app.auth().currentUser.getIdToken(false).then(function (Token) {
      axios.get(userdetailapi)
      .then(r => {
        setUser(r)
        setUserDetail(r)
      })
    // })

  }, [addform, updateuser])

  useEffect(() => {
    const {location, history} = props
    //use the state via location.state
    //and replace the state via
    history.replace() 
  }, [history])
console.log(userdetail)
  return (
    <div className='auth-wrapper auth-v1 px-2'>
      <div className='auth-inner py-2'>
      { addform === true ?   <Card className='mb-0'>
          <CardBody>
            <CardTitle tag='h4' className='mb-1'>
            Stationary Request
            </CardTitle>
            {/* <CardText className='mb-2'>Please Register for master</CardText> */}
           <MasterForm onAdd={() => { setAddForm(false) }} />
          </CardBody>
        </Card> : " "}
        {updateuser === true ?   <Card className='mb-0'>
          <CardBody>
            <CardTitle tag='h4' className='mb-1'>
            Stationary Request
            </CardTitle>
            {/* <CardText className='mb-2'>Please Register for master</CardText> */}
            <MasterForm
             preloadValue={props && props.location && props.location.state} onAdd={() => { setUpdateuser(false) }}
             />
          </CardBody>
        </Card> : "" }
      
        <div style={{padding:10}}>
     
        </div>
        <Card className='mb-0'>
          <CardBody> 
          <Row>
              <Col md={3}>
              <h4 style={{margin:'10px'}} className='mb-1'>Stationary Request</h4>
              </Col>
              <Col md={2}>
                  <h6 style={{float:"right", marginTop:"10px"}}>Search</h6>
                  </Col>
              <Col md={3}>
            <FormGroup>
                <Input
                 type='search'
                 onChange = {(e) => searchChange(e)}
                 id='code'
                name="image"
                //   className={classnames({ 'is-invalid': errors['image'] })}
                //   innerRef={register({ required: true, validate: value => value !== '' })}
                 placeholder='Search Updated List' autoFocus />
              </FormGroup>
              </Col>
              <Col md={4}>
              {addbutton === false ?   <Button.Ripple className='ripple-button' type="submit" size="sm" onClick={onAddClick} style={{margin:'10px', float:"right"}}>
          <i class="fa fa-plus"></i> Add Stationary Request
              </Button.Ripple> : <Button.Ripple className='ripple-button' type="submit" size="sm" onClick={onCancleClick} style={{margin:'10px', float:"right"}}>
          <i class="fa fa-minus"></i> Add Stationary Request
              </Button.Ripple>
              
            }
              </Col>
            </Row>
  
    <TableContainer className={classes.container}>
        <Table stickyHeader size="small" aria-label="a dense table">
          <TableHead>
            <TableRow>
              {columns.map((column) => (
                <TableCell
                  key={column.id}
                  align={column.align}
                  style={{ minWidth: column.minWidth }}
                >
                  {column.label}
                </TableCell>
              ))}
            </TableRow>
          </TableHead>
          <TableBody>
            {userdetail && userdetail.data && userdetail.data.data && userdetail.data.data.slice((page * rowsPerPage), (page * rowsPerPage) + rowsPerPage).map((detail, index) => {
              console.log(detail)
              return (
                <TableRow hover role="checkbox" tabIndex={-1}>
                      <TableCell>
                    {index + 1}
                      </TableCell>
                      <TableCell>
                      {detail.location === null ? 'Not found' : detail.location.location_name}
                      </TableCell>
                      <TableCell>
                      {detail.purchase_type === null ? 'Not found' : detail.purchase_type.document_type_name}
                      </TableCell>
                      <TableCell>
                      {detail.currency === null ? 'Not found' : detail.currency.currency_description}
                      </TableCell>
                      <TableCell>
                      {detail.vendor_name}
                      </TableCell>
                      <TableCell>
                      {moment(detail.purchase_request_date).format("YYYY-DD-MM")}
                      </TableCell>
                      <TableCell>
                      {moment(detail.validity_date).format("YYYY-DD-MM")}
                      </TableCell>
                      <TableCell>
                      {moment(detail.required_date).format("YYYY-DD-MM")}
                      </TableCell>
                      <TableCell>
                      {detail.item_description}
                      </TableCell>
                      <TableCell>
                      {detail.quantity}
                      </TableCell>
                      <TableCell>
                      {detail.uom === null ? 'Not Found' : detail.uom.uom_description}
                      </TableCell>
                      <TableCell>
                      {detail.rate}
                      </TableCell>
                      <TableCell>
                      {detail.tax_rate}
                      </TableCell>
                      <TableCell>
                      {detail.remarks}
                      </TableCell>
                      <TableCell>
                      {detail.remark}
                      </TableCell>
                      <TableCell>
                      {detail.attachment_file}
                      </TableCell>
                      <TableCell>
                      {detail.status === true ? <Switch
        checked={true}
        color="primary"
        name="checkedB"
        inputProps={{ 'aria-label': 'primary checkbox' }}
      /> : <Switch
      checked={false}
      name="checkedA"
      inputProps={{ 'aria-label': 'secondary checkbox' }}
    />}
                      </TableCell>
                      <TableCell>
                      <Button.Ripple className='btn-icon btn-sm rounded-circle' color='flat-success'  onClick={() => onEditClick(detail, props)} outline id={`positionTop_${index + 1}`}>
        <Edit2 size={16} />
      </Button.Ripple>
      <UncontrolledTooltip placement='top' target={`positionTop_${index + 1}`}>
       Edit
      </UncontrolledTooltip>
                      </TableCell>
                </TableRow>
              )
            })}
          </TableBody>
        </Table>
      </TableContainer>
      <TablePagination
        rowsPerPageOptions={[10, 20, 30]}
        component="div"
        count={userdetail && userdetail.data && userdetail.data.data && userdetail.data.data.length}
        rowsPerPage={rowsPerPage}
        page={page}
        onPageChange={handleChangePage}
        onRowsPerPageChange={handleChangeRowsPerPage}
      />

    </CardBody>
    </Card>
      </div>
    </div>
  )
}

export default StatinaryMaster
