import { useState, useContext, Fragment, useEffect } from 'react'
import { Link } from 'react-router-dom'
import { Facebook, Twitter, Mail, GitHub, Edit2} from 'react-feather'
import InputPasswordToggle from '@components/input-password-toggle'
import {Col, Row, Card, CardBody, CardTitle, CardText, Form, FormGroup, Label, Input, CustomInput, Button, UncontrolledTooltip } from 'reactstrap'
import { useForm } from 'react-hook-form'
import classnames from 'classnames'
import { useDispatch } from 'react-redux'
import { add_BusinessTrip, update_BusinessTrip } from '@store/actions/business'
import axios from 'axios'
// import '@styles/base/pages/page-auth.scss'
import { makeStyles } from '@material-ui/core/styles'
import Paper from '@material-ui/core/Paper'
import Table from '@material-ui/core/Table'
import TableBody from '@material-ui/core/TableBody'
import TableCell from '@material-ui/core/TableCell'
import TableContainer from '@material-ui/core/TableContainer'
import TableHead from '@material-ui/core/TableHead'
import TablePagination from '@material-ui/core/TablePagination'
import TableRow from '@material-ui/core/TableRow'
const moment = require('moment')
const loginAuthID = sessionStorage.getItem('id_token')
const columns = [
  { id: 'index', label: 'Sr. No.', minWidth: 90 },
  {
    id: 'location',
    label: 'Location',
    minWidth: 170,
    align: 'right',
    format: (value) => value.toLocaleString('en-US')
  },
  {
    id: 'business_trip_type',
    label: 'Business trip type',
    minWidth: 200,
    align: 'right',
    format: (value) => value.toLocaleString('en-US')
  },
  {
    id: 'from_place',
    label: 'From place',
    minWidth: 170,
    align: 'right',
    format: (value) => value.toLocaleString('en-US')
  },
  {
    id: 'to_place',
    label: 'To place',
    minWidth: 170,
    align: 'right',
    format: (value) => value.toLocaleString('en-US')
  },
  {
    id: 'from_date',
    label: 'From date',
    minWidth: 170,
    align: 'right',
    format: (value) => value.toLocaleString('en-US')
  },
  {
    id: 'to_date',
    label: 'To date',
    minWidth: 170,
    align: 'right',
    format: (value) => value.toLocaleString('en-US')
  },
  {
    id: 'departure_date',
    label: 'Departure date',
    minWidth: 170,
    align: 'right',
    format: (value) => value.toLocaleString('en-US')
  },
  {
    id: 'departure_time',
    label: 'Departure time',
    minWidth: 170,
    align: 'right',
    format: (value) => value.toLocaleString('en-US')
  },
  {
    id: 'return_date',
    label: 'Return date',
    minWidth: 170,
    align: 'right',
    format: (value) => value.toLocaleString('en-US')
  },
  {
    id: 'return_time',
    label: 'Return time',
    minWidth: 170,
    align: 'right',
    format: (value) => value.toLocaleString('en-US')
  },
  {
    id: 'mode_of_transport',
    label: 'Mode of transport',
    minWidth: 170,
    align: 'right',
    format: (value) => value.toLocaleString('en-US')
  },
  {
    id: 'rental_car_required',
    label: 'Rental car required',
    minWidth: 170,
    align: 'right',
    format: (value) => value.toLocaleString('en-US')
  },
  {
    id: 'planned_food',
    label: 'Planned food',
    minWidth: 170,
    align: 'right',
    format: (value) => value.toLocaleString('en-US')
  },
  {
    id: 'advance_required',
    label: 'Advance required',
    minWidth: 170,
    align: 'right',
    format: (value) => value.toLocaleString('en-US')
  },
  {
    id: 'purpose',
    label: 'Purpose',
    minWidth: 170,
    align: 'right',
    format: (value) => value.toLocaleString('en-US')
  },
  {
    id: 'advance_amount',
    label: 'Advance_amount',
    minWidth: 170,
    align: 'right',
    format: (value) => value.toLocaleString('en-US')
  },
  { id: 'action', label: 'ACTION', minWidth: 100 }
]


const useStyles = makeStyles({
  root: {
    width: '100%'
  },
  container: {
    maxHeight: 440
  }
})
const MasterForm = (props) => {
    const { register, errors, setValue, handleSubmit } = useForm({defaultValues:props ? props && props.preloadValue && props.preloadValue.detail : ""})
   const [organization, setOrganization] = useState([])
    const dispatch = useDispatch()
// console.log("country id", countryid)
useEffect(() => {
  setValue("departure_date", moment(props && props.preloadValue && props.preloadValue.detail.departure_date).format("YYYY-MM-DD"))
  setValue("from_date", moment(props && props.preloadValue && props.preloadValue.detail.from_date).format("YYYY-MM-DD"))
  setValue("return_date", moment(props && props.preloadValue && props.preloadValue.detail.return_date).format("YYYY-MM-DD"))
  setValue("return_time", moment(props && props.preloadValue && props.preloadValue.detail.return_time).format("HH:mm a"))
  setValue("departure_time", moment(props && props.preloadValue && props.preloadValue.detail.departure_time).format("HH:mm a"))
  setValue("to_date", moment(props && props.preloadValue && props.preloadValue.detail.to_date).format("YYYY-MM-DD"))
}, [props])

const organizations = 'http://172.105.49.15:3030/api/master/list_organization'
useEffect(() => {
  axios.get(organizations)
  .then(data => setOrganization(data.data)
    )
}, [organizations])
    // console.log(state)
    const onSubmit = data => {
        // alert("SUBMITED SUCCESSFULLY")
        if (props && props.preloadValue) { 
          dispatch(update_BusinessTrip(data, props))
        } else {
          dispatch(add_BusinessTrip(data, props))
        }
      }

    console.log(props)
return (
  <div>
 <Form className='auth-login-form mt-2' onSubmit={handleSubmit(onSubmit)}>
            <Row>
            <Col md={3}>
            <FormGroup>
                <Label className='form-label'>
                location
                </Label>
                <Input
                 type='select'
                 id='location'
                name="location"
                  className={classnames({ 'is-invalid': errors['location'] })}
                  innerRef={register({ required: true, validate: value => value !== '' })}
                 placeholder='Select your location' autoFocus >
                 {organization && organization.data && organization.data.map((org, index) => {
                   return (
                     <option value={org._id}>{org.location_name}</option>
                   )
                 })}
                 </Input>
              </FormGroup>
      {errors.location && errors.location.type === 'required' && <p style={{color:"red"}}>location is required</p>}

              </Col>

              <Col md={3}>
              <FormGroup>
                <Label className='form-label'>
                Business trip type
                </Label>
                <Input
                 type='text'
                //  value={email}
                 id='business_trip_type'
                 name='business_trip_type'
                //  onChange={e => setEmail(e.target.value)}
                  className={classnames({ 'is-invalid': errors['business_trip_type'] })}
                  innerRef={register({ required: true, validate: value => value !== '' })}
                placeholder='Enter business trip type'
                 autoFocus/>
              </FormGroup>
                {errors.business_trip_type && errors.business_trip_type.type === 'required' && <p style={{color:"red"}}>business trip type is required</p>}
              </Col>

              <Col md={3}>
              <FormGroup>
                <Label className='form-label'>
                From place
                </Label>
                <Input
                 type='text'
                //  value={email}
                 id='from_place'
                 name='from_place'
                //  onChange={e => setEmail(e.target.value)}
                  className={classnames({ 'is-invalid': errors['from_place'] })}
                  innerRef={register({ required: true, validate: value => value !== '' })}
                placeholder='Enter from place'
                 autoFocus/>
              </FormGroup>
                {errors.from_place && errors.from_place.type === 'required' && <p style={{color:"red"}}>from place is required</p>}
              </Col>

              <Col md={3}>
              <FormGroup>
                <Label className='form-label'>
                To place
                </Label>
                <Input
                 type='text'
                //  value={email}
                 id='to_place'
                 name='to_place'
                //  onChange={e => setEmail(e.target.value)}
                  className={classnames({ 'is-invalid': errors['to_place'] })}
                  innerRef={register({ required: true, validate: value => value !== '' })}
                placeholder='Enter to place'
                 autoFocus/>
              </FormGroup>
                {errors.to_place && errors.to_place.type === 'required' && <p style={{color:"red"}}>To place is required</p>}
              </Col>

              <Col md={3}>
              <FormGroup>
                <Label className='form-label'>
                  From date
                </Label>
                <Input
                 type='date'
                //  value={email}
                 id='from_date'
                 name='from_date'
                //  onChange={e => setEmail(e.target.value)}
                  className={classnames({ 'is-invalid': errors['from_date'] })}
                  innerRef={register({ required: true, validate: value => value !== '' })}
                placeholder='Enter from date'
                 autoFocus/>
              </FormGroup>
                {errors.from_date && errors.from_date.type === 'required' && <p style={{color:"red"}}>From date is required</p>}
              </Col>

              <Col md={3}>
              <FormGroup>
                <Label className='form-label'>
                To date
                </Label>
                <Input
                 type='date'
                //  value={email}
                 id='to_date'
                 name='to_date'
                //  onChange={e => setEmail(e.target.value)}
                  className={classnames({ 'is-invalid': errors['to_date'] })}
                  innerRef={register({ required: true, validate: value => value !== '' })}
                placeholder='Enter to date'
                 autoFocus/>
              </FormGroup>
                {errors.to_date && errors.to_date.type === 'required' && <p style={{color:"red"}}>to date is required</p>}
              </Col>

              <Col md={3}>
              <FormGroup>
                <Label className='form-label'>
                Departure date
                </Label>
                <Input
                 type='date'
                //  value={email}
                 id='departure_date'
                 name='departure_date'
                //  onChange={e => setEmail(e.target.value)}
                  className={classnames({ 'is-invalid': errors['departure_date'] })}
                  innerRef={register({ required: true, validate: value => value !== '' })}
                placeholder='Enter departure date'
                 autoFocus/>
              </FormGroup>
                {errors.departure_date && errors.departure_date.type === 'required' && <p style={{color:"red"}}>departure date is required</p>}
              </Col>

              <Col md={3}>
              <FormGroup>
                <Label className='form-label'>
                departure time
                </Label>
                <Input
                 type='time'
                 format="HH:mm a"
                //  value={email}
                 id='departure_time'
                 name='departure_time'
                //  onChange={e => setEmail(e.target.value)}
                  className={classnames({ 'is-invalid': errors['departure_time'] })}
                  innerRef={register({ required: true, validate: value => value !== '' })}
                placeholder='Enter departure time'
                 autoFocus/>
              </FormGroup>
                {errors.departure_time && errors.departure_time.type === 'required' && <p style={{color:"red"}}>departure time is required</p>}
              </Col>

              <Col md={3}>
              <FormGroup>
                <Label className='form-label'>
                Return date
                </Label>
                <Input
                 type='date'
                //  value={email}
                 id='return_date'
                 name='return_date'
                //  onChange={e => setEmail(e.target.value)}
                  className={classnames({ 'is-invalid': errors['return_date'] })}
                  innerRef={register({ required: true, validate: value => value !== '' })}
                placeholder='Enter return date'
                 autoFocus/>
              </FormGroup>
                {errors.return_date && errors.return_date.type === 'required' && <p style={{color:"red"}}>return date is required</p>}
              </Col>

              <Col md={3}>
              <FormGroup>
                <Label className='form-label'>
                Return time
                </Label>
                <Input
                 type='time'
                //  value={email}
                 id='return_time'
                 name='return_time'
                //  onChange={e => setEmail(e.target.value)}
                  className={classnames({ 'is-invalid': errors['return_time'] })}
                  innerRef={register({ required: true, validate: value => value !== '' })}
                placeholder='Enter return time'
                 autoFocus/>
              </FormGroup>
                {errors.return_time && errors.return_time.type === 'required' && <p style={{color:"red"}}>return time is required</p>}
              </Col>

              <Col md={3}>
              <FormGroup>
                <Label className='form-label'>
                Mode of transport
                </Label>
                <Input
                 type='text'
                //  value={email}
                 id='mode_of_transport'
                 name='mode_of_transport'
                //  onChange={e => setEmail(e.target.value)}
                  className={classnames({ 'is-invalid': errors['mode_of_transport'] })}
                  innerRef={register({ required: true, validate: value => value !== '' })}
                placeholder='Enter mode of transport'
                 autoFocus/>
              </FormGroup>
                {errors.mode_of_transport && errors.mode_of_transport.type === 'required' && <p style={{color:"red"}}>mode of transport is required</p>}
              </Col>

              <Col md={3}>
              <FormGroup>
                <Label className='form-label'>
                rental_car_required
                </Label>
                <Input
                 type='text'
                //  value={email}
                 id='rental_car_required'
                 name='rental_car_required'
                //  onChange={e => setEmail(e.target.value)}
                  className={classnames({ 'is-invalid': errors['rental_car_required'] })}
                  innerRef={register({ required: true, validate: value => value !== '' })}
                placeholder='Enter rental car required'
                 autoFocus/>
              </FormGroup>
                {errors.rental_car_required && errors.rental_car_required.type === 'required' && <p style={{color:"red"}}>rental car required is required</p>}
              </Col>

              <Col md={3}>
              <FormGroup>
                <Label className='form-label'>
                Planned food
                </Label>
                <Input
                 type='text'
                //  value={email}
                 id='planned_food'
                 name='planned_food'
                //  onChange={e => setEmail(e.target.value)}
                  className={classnames({ 'is-invalid': errors['planned_food'] })}
                  innerRef={register({ required: true, validate: value => value !== '' })}
                placeholder='Enter planned food'
                 autoFocus/>
              </FormGroup>
                {errors.planned_food && errors.planned_food.type === 'required' && <p style={{color:"red"}}>planned food is required</p>}
              </Col>

              <Col md={3}>
              <FormGroup>
                <Label className='form-label'>
                Advance required
                </Label>
                <Input
                 type='text'
                //  value={email}
                 id='advance_required'
                 name='advance_required'
                //  onChange={e => setEmail(e.target.value)}
                  className={classnames({ 'is-invalid': errors['advance_required'] })}
                  innerRef={register({ required: true, validate: value => value !== '' })}
                placeholder='Enter advance required'
                 autoFocus/>
              </FormGroup>
                {errors.advance_required && errors.advance_required.type === 'required' && <p style={{color:"red"}}>advance required is required</p>}
              </Col>

              <Col md={3}>
              <FormGroup>
                <Label className='form-label'>
                purpose
                </Label>
                <Input
                 type='text'
                //  value={email}
                 id='purpose'
                 name='purpose'
                //  onChange={e => setEmail(e.target.value)}
                  className={classnames({ 'is-invalid': errors['purpose'] })}
                  innerRef={register({ required: true, validate: value => value !== '' })}
                placeholder='Enter purpose'
                 autoFocus/>
              </FormGroup>
                {errors.purpose && errors.purpose.type === 'required' && <p style={{color:"red"}}>purpose is required</p>}
              </Col>

              <Col md={3}>
              <FormGroup>
                <Label className='form-label'>
                advance_amount
                </Label>
                <Input
                 type='number'
                //  value={email}
                 id='advance_amount'
                 name='advance_amount'
                //  onChange={e => setEmail(e.target.value)}
                  className={classnames({ 'is-invalid': errors['advance_amount'] })}
                  innerRef={register({ required: true, validate: value => value !== '' })}
                placeholder='Enter advance amount'
                 autoFocus/>
              </FormGroup>
                {errors.advance_amount && errors.advance_amount.type === 'required' && <p style={{color:"red"}}>advance amount is required</p>}
              </Col>
              <Col md={12}>
              <span className="input-group-btn">
          <Button.Ripple  type="submit" className='ripple-button  btn-theam theam_m' size="sm" style={{float:"right"}}>
          { props.preloadValue ? 'Update' : 'Submit' }
              </Button.Ripple>
              </span>
              </Col>
              </Row>
              {/* { props.preloadValue ?    <Button.Ripple color='primary' type="submit" className='ripple-button btn-theam' size="sm" style={{float:"right"}} >
               Update
              </Button.Ripple> :   <Button.Ripple color='primary' type="submit" className='ripple-button btn-theam' size="sm" style={{float:"right"}} >
                Save
              </Button.Ripple>} */}
            
            </Form>

  </div>
)
}

const BusinessTrip = (props) => {
  const [addform, setAddForm] = useState(false)
  const [updateuser, setUpdateuser] = useState(false)
  const [addbutton, setAddButton] = useState(false)
  const [userdetail, setUserDetail] = useState()
  const [user, setUser] = useState([])
  const classes = useStyles()
  const [page, setPage] = useState(0)
  const [rowsPerPage, setRowsPerPage] = useState(10)
  const [searching, setSearchStream] = useState('')
  const handleChangePage = (event, newPage) => {
    setPage(newPage)
  }

  const handleChangeRowsPerPage = (event) => {
    setRowsPerPage(+event.target.value)
    setPage(0)
  }
 ///search table data
const searchChange = (e) => {
 e.preventDefault()
 const searchstream = e.target.value
//  setSearchStream(searchstream)
//  const searchString = searching
if (searchstream.length > 0) {
  const newstring = new RegExp(searchstream)
       const searchitem = userdetail && userdetail.data && userdetail.data.data && userdetail.data.data.filter(item => {
         console.log(item)
         return item.business_trip_type.match(searchstream) || item.from_place.match(newstring) || item.to_place.match(newstring) ||
         moment(item.from_date).format("YYYY-MM-DD").match(newstring) || moment(item.to_date).format("YYYY-MM-DD").match(newstring) ||
         moment(item.departure_date).format("YYYY-MM-DD").match(newstring) || moment(item.departure_time).format("HH:mm").match(newstring) ||
         moment(item.return_date).format("YYYY-MM-DD").match(newstring) || moment(item.return_time).format("HH:mm").match(newstring) ||
         item.mode_of_transport.match(searchstream) || item.rental_car_required.match(searchstream) || item.planned_food.match(searchstream) ||
         item.advance_required.match(searchstream) || item.purpose.match(searchstream) || item.advance_amount.toString().match(searchstream)
       })
       console.log(searchitem)
       setUserDetail({data:{data:searchitem}})
      } else {
        setUserDetail(user)
      }
}
  const onAddClick = () => {
    setAddForm(true)
    setAddButton(true)
    setUpdateuser(false)
  }
  const onCancleClick = () => {
    setAddForm(false)
    setAddButton(false)
  }

  const onEditClick = (detail) => {
    props.history.push({
      state : {
     detail
     }})
     setUpdateuser(true)
     setAddForm(false)
  }
  const userdetailapi = 'http://172.105.49.15:3030/api/business/list_business_trip_request'
  useEffect(() => {
    axios.get(userdetailapi, {
      headers:{
        token:loginAuthID
      }
    })
    .then(r => { 
      setUserDetail(r)
      setUser(r)
    })
  }, [userdetailapi, addform, updateuser])
console.log(userdetail)
  return (
    <div className='auth-wrapper auth-v1 px-2'>
      <div className='auth-inner py-2'>
      { addform === true ?   <Card className='mb-0'>
          <CardBody>
            <CardTitle tag='h4' className='mb-1'>
            Business Trip
            </CardTitle>
            {/* <CardText className='mb-2'>Please Register for master</CardText> */}
           <MasterForm onAdd={() => { setAddForm(false) }} onMasterSave={() => setAddButton(false)}/>
          </CardBody>
        </Card> : " "}
        {updateuser === true ?   <Card className='mb-0'>
          <CardBody>
            <CardTitle tag='h4' className='mb-1'>
            Business Trip
            </CardTitle>
            {/* <CardText className='mb-2'>Please Register for master</CardText> */}
            <MasterForm
             preloadValue={props && props.location && props.location.state} onAdd={() => { setUpdateuser(false) }}
             onMasterSave={() => setAddButton(false)}
             />
          </CardBody>
        </Card> : "" }
      
        <div style={{padding:10}}>
     
        </div>
        <Card className='mb-0'>
          <CardBody> 
          <Row>
              <Col md={3}>
              <h4 style={{margin:'10px'}} className='mb-1'>Trip List</h4>
              </Col>
              <Col md={2}>
                  <h6 style={{float:"right", marginTop:"10px"}}>Search</h6>
                  </Col>
              <Col md={3}>
            <FormGroup>
                <Input 
                type='search'
                onChange ={(e) => {
                  searchChange(e)
                }}
                id='code'
                name="image"
                //   className={classnames({ 'is-invalid': errors['image'] })}
                //   innerRef={register({ required: true, validate: value => value !== '' })}
                 placeholder='search...' autoFocus />
              </FormGroup>
              </Col>
              <Col md={4}>
              {addbutton === false ?   <Button.Ripple className='ripple-button btn-theam' type="submit" size="sm" onClick={onAddClick} style={{margin:'10px', float:"right"}}>
          <i class="fa fa-plus"></i> Add Trip
              </Button.Ripple> : <Button.Ripple className='ripple-button btn-theam' type="submit" size="sm" onClick={onCancleClick} style={{margin:'10px', float:"right"}}>
          <i class="fa fa-minus"></i> Add Trip
              </Button.Ripple>
              
            }
              </Col>
            </Row>
        
    <TableContainer className={classes.container}>
        <Table stickyHeader size="small" aria-label="a dense table">
          <TableHead>
            <TableRow>
              {columns.map((column) => (
                <TableCell
                  key={column.id}
                  align={column.align}
                  style={{ minWidth: column.minWidth }}
                >
                  {column.label}
                </TableCell>
              ))}
            </TableRow>
          </TableHead>
          <TableBody>
            {userdetail && userdetail.data && userdetail.data.data && userdetail.data.data.slice((page * rowsPerPage), (page * rowsPerPage) + rowsPerPage).map((detail, index) => {
            
              return (
                <TableRow hover role="checkbox" tabIndex={-1}>
                      <TableCell >
                    {index + 1}
                      </TableCell>
                      <TableCell  >
                      {detail.location === null ? 'not found' : detail.location.location_name}
                      </TableCell>
                      <TableCell  >
                      {detail.business_trip_type}
                      </TableCell>
                      <TableCell  >
                      {detail.from_place}
                      </TableCell>
                      <TableCell  >
                      {detail.to_place}
                      </TableCell>
                      <TableCell  >
                      {moment(detail.from_date).format("YYYY-MM-DD")}
                      </TableCell>
                      <TableCell  >
                      {moment(detail.to_date).format("YYYY-MM-DD")}
                      </TableCell>
                      <TableCell  >
                      {moment(detail.departure_date).format("YYYY-MM-DD")}
                      </TableCell>
                      <TableCell  >
                      {moment(detail.departure_time).format("HH:mm a")}
                      {/* {detail.departure_time.toLocaleString('en-US', { hour: 'numeric', hour12: true })} */}
                      </TableCell>
                      <TableCell  >
                      {moment(detail.return_date).format("YYYY-MM-DD")}
                      </TableCell>
                      <TableCell  >
                      {/* {moment(detail.return_time).format("HH:mm a")} */}
                      {detail.return_time}
                      </TableCell>
                      <TableCell  >
                      {detail.mode_of_transport}
                      </TableCell>
                      <TableCell  >
                      {detail.rental_car_required}
                      </TableCell>
                      <TableCell  >
                      {detail.planned_food}
                      </TableCell>
                      <TableCell  >
                      {detail.advance_required}
                      </TableCell>
                      <TableCell  >
                      {detail.purpose}
                      </TableCell>
                      <TableCell  >
                      {detail.advance_amount}
                      </TableCell>
                      <TableCell >
                      <Button.Ripple className='btn-icon btn-sm rounded-circle' color='flat-success'  onClick={() => onEditClick(detail, props)} outline id={`positionTop_${index + 1}`}>
        <Edit2 size={16} />
      </Button.Ripple>
      <UncontrolledTooltip placement='top' target={`positionTop_${index + 1}`}>
       Edit
      </UncontrolledTooltip>
                      </TableCell>
                </TableRow>
              )
            })}
          </TableBody>
        </Table>
      </TableContainer>
      <TablePagination
        rowsPerPageOptions={[10, 20, 30]}
        component="div"
        count={userdetail && userdetail.data && userdetail.data.data && userdetail.data.data.length}
        rowsPerPage={rowsPerPage}
        page={page}
        onPageChange={handleChangePage}
        onRowsPerPageChange={handleChangeRowsPerPage}
      />
  
    </CardBody>
    </Card>
      </div>
    </div>
  )
}

export default BusinessTrip
