import { useState, useContext, Fragment, useEffect } from 'react'
import { Link } from 'react-router-dom'
import { Facebook, Twitter, Mail, GitHub, Edit2 } from 'react-feather'
import InputPasswordToggle from '@components/input-password-toggle'
import {Col, Row, Card, CardBody, CardTitle, CardText, Form, FormGroup, UncontrolledTooltip, Label, Input, CustomInput, Button } from 'reactstrap'
import { useForm } from 'react-hook-form'
import classnames from 'classnames'
import { useDispatch } from 'react-redux'
import { add_Purchase_Request, update_Purchase_Request } from '@store/actions/purchase/purchaseRequest'
import axios from 'axios'
// import '@styles/base/pages/page-auth.scss'
import { makeStyles } from '@material-ui/core/styles'
import Paper from '@material-ui/core/Paper'
import Divider from '@material-ui/core/Divider'
import { green, red } from '@material-ui/core/colors'
import Switch from '@material-ui/core/Switch'
import Table from '@material-ui/core/Table'
import TableBody from '@material-ui/core/TableBody'
import TableCell from '@material-ui/core/TableCell'
import TableContainer from '@material-ui/core/TableContainer'
import TableHead from '@material-ui/core/TableHead'
import TablePagination from '@material-ui/core/TablePagination'
import TableRow from '@material-ui/core/TableRow'
import axiosConfig from './../../../axiosConfig'
import app from './../../../base'
import { uploadFile } from 'react-s3'

const config = {
  bucketName: 'ilovenagar',
  dirName: 'ilovenagar', /* optional */
  region: 'ap-south-1',
  accessKeyId: 'AKIARS26YOAJD4KZJDNK',
  secretAccessKey: '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'
}
const moment = require('moment')
const loginAuthID = sessionStorage.getItem('id_token')
const columns = [
  { id: 'index', label: 'Sr. No.', minWidth: 100 },
  {
    id: 'location',
    label: 'Location',
    minWidth: 170,
    align: 'right',
    format: (value) => value.toLocaleString('en-US')
  },
  {
    id: 'purchase_type',
    label: 'purchase_type',
    minWidth: 170,
    align: 'right',
    format: (value) => value.toLocaleString('en-US')
  },
  {
    id: 'currency',
    label: 'currency',
    minWidth: 170,
    align: 'right',
    format: (value) => value.toLocaleString('en-US')
  },
  {
    id: 'vendor_name',
    label: 'vendor_name',
    minWidth: 170,
    align: 'right',
    format: (value) => value.toLocaleString('en-US')
  },
  {
    id: 'purchase_Request_Date',
    label: 'purchase_Request_Date',
    minWidth: 170,
    align: 'right',
    format: (value) => value.toLocaleString('en-US')
  },
  {
    id: 'validity_date',
    label: 'validity_date',
    minWidth: 170,
    align: 'right',
    format: (value) => value.toLocaleString('en-US')
  },
  {
    id: 'required_date',
    label: 'required_date',
    minWidth: 170,
    align: 'right',
    format: (value) => value.toLocaleString('en-US')
  },
  {
    id: 'item_description',
    label: 'item_description',
    minWidth: 170,
    align: 'right',
    format: (value) => value.toLocaleString('en-US')
  },
  {
    id: 'quantity',
    label: 'quantity',
    minWidth: 170,
    align: 'right',
    format: (value) => value.toLocaleString('en-US')
  },
  {
    id: 'uom',
    label: 'uom',
    minWidth: 170,
    align: 'right',
    format: (value) => value.toLocaleString('en-US')
  },
  {
    id: 'rate',
    label: 'rate',
    minWidth: 170,
    align: 'right',
    format: (value) => value.toLocaleString('en-US')
  },
  {
    id: 'tax_rate',
    label: 'tax_rate',
    minWidth: 170,
    align: 'right',
    format: (value) => value.toLocaleString('en-US')
  },
  {
    id: 'remarks',
    label: 'remarks',
    minWidth: 170,
    align: 'right',
    format: (value) => value.toLocaleString('en-US')
  },
  {
    id: 'remark',
    label: 'remark',
    minWidth: 170,
    align: 'right',
    format: (value) => value.toLocaleString('en-US')
  },
  {
    id: 'attachment_file',
    label: 'attachment_file',
    minWidth: 170,
    align: 'right',
    format: (value) => value.toLocaleString('en-US')
  },
  {
    id: 'status',
    label: 'status',
    minWidth: 170,
    align: 'right',
    format: (value) => value.toLocaleString('en-US')
  }
]


const useStyles = makeStyles({
  root: {
    width: '100%'
  },
  container: {
    maxHeight: 440
  },
  titleColor :{
    color: "#d52528"
  }
})
const MasterForm = (props) => {
    const { register, errors, handleSubmit, setValue } = useForm({defaultValues:props ? props && props.preloadValue && props.preloadValue.detail : ""})
    const [PurchaseType, setPurchaseType] = useState('')
    const [currency, setCurrency] = useState('')
    const [location, setLocation] = useState('')
    const [primaryuom, setPrimaryUOM] = useState('')
    const classes = useStyles()
    const [categorybyid, setCategorybyid] = useState('')
    const [categoryState, setCategoryState] = useState('')
    const dispatch = useDispatch()
    const [indexes, setIndexes] = useState([])
    const [counter, setCounter] = useState(0)
    const [bucketimage, setImage] = useState()
    const [updatedImage, setUpdatedImage] = useState(props && props.preloadValue && props.preloadValue.detail)

    useEffect(() => {
      setValue("purchase_request_date", moment(props && props.preloadValue && props.preloadValue.detail.purchase_request_date).format("YYYY-MM-DD"))
      setValue("validity_date", moment(props && props.preloadValue && props.preloadValue.detail.validity_date).format("YYYY-MM-DD"))
      setValue("required_date", moment(props && props.preloadValue && props.preloadValue.detail.required_date).format("YYYY-MM-DD"))
    }, [setValue])


    const addFriend = () => {
      setIndexes(prevIndexes => [...prevIndexes, counter])
      setCounter(prevCounter => prevCounter + 1)
    }
  
    const removeFriend = index => () => {
      setIndexes(prevIndexes => [...prevIndexes.filter(item => item !== index)])
      setCounter(prevCounter => prevCounter - 1)
    }
    
    const uploadImage = (e) => {
      console.log(e.target.files[0])
      uploadFile(e.target.files[0], config)
    .then(data => {
      setImage(data)
      setValue("attachment_file", data.location)
    })
    .catch(err => console.error(err))
    }
    const  organization = '/api/master/list_organization'
    useEffect(() => {
      axiosConfig.get(organization)
    .then(r => {
      console.log(r)
      setLocation(r)
    })
    }, [organization])

    const  currencyType = '/api/master/list_Currency_Master'
    useEffect(() => {
      axiosConfig.get(currencyType)
    .then(r => {
      console.log(r)
      setCurrency(r)
    })
    }, [currencyType])

    const  purchaseType = '/api/master/list_document_type_master'
    useEffect(() => {
      axiosConfig.get(purchaseType)
    .then(r => {
      console.log(r)
      setPurchaseType(r)
    })
    }, [purchaseType])

    const  primUOM = '/api/master/list_Unit_of_Measurement'
    useEffect(() => {
    axiosConfig.get(primUOM, {
    headers:{
      token:loginAuthID
    }
    })
    .then(r => {
      console.log(r)
      setPrimaryUOM(r)
    })
    }, [primUOM])

    const onSubmit = data => {
      console.log(data)
        // alert("SUBMITED SUCCESSFULLY")
        if (props && props.preloadValue && props.preloadValue.detail._id) { 
          dispatch(update_Purchase_Request(data, props))
        } else {
          dispatch(add_Purchase_Request(data, props))
        }
      }

    console.log(props)
return (
  <div>
  <div>  
<Form className='auth-login-form mt-2' onSubmit={handleSubmit(onSubmit)}>
            <Row>
              <Col md={3}>
              <FormGroup>
                <Label>
                Currency
                </Label>
                <Input
                 type='select'
                 id='currency'
                 name='currency'
                  className={classnames({ 'is-invalid': errors['currency'] })}
                  innerRef={register({ required: true, validate: value => value !== '' })}
                placeholder='Enter Your material code'
                 autoFocus >
                    {currency && currency.data && currency.data.data && currency.data.data.map((detail, index) => {
                   return (
                    <option value ={detail._id}>{detail.currency_description}</option>
                    )
                  })}
                  </Input>
              </FormGroup>
              {errors.currency  && errors.currency.type === 'required' && <p style={{color:"red"}}>currency is required</p>}

              </Col>
              <Col md={3}>
              <FormGroup>
                <Label>
                Vendor name
                </Label>
                <Input
                 type='text'
                 id='vendor_name'
                 name='vendor_name'
                  className={classnames({ 'is-invalid': errors['vendor_name'] })}
                  innerRef={register({ required: true, validate: value => value !== '' })}
                placeholder='Enter Your Designation'
                 autoFocus />
              </FormGroup>
              {errors.vendor_name && errors.vendor_name.type === 'required' && <p style={{color:"red"}}>Material description is required</p>}
              </Col>
              <Col md={3}>
              <FormGroup>
                <Label>
                Location
                </Label>
                <Input
                 type='select'
                 id='location'
                 name='location'
                  className={classnames({ 'is-invalid': errors['location'] })}
                  innerRef={register({ required: true, validate: value => value !== '' })}
                placeholder='Enter your location'
                 autoFocus >
            {location && location.data && location.data.data && location.data.data.map((detail, index) => {
                   return (
                    <option value ={detail._id}>{detail.location_name}</option>
                    )
                  })}
                  </Input>
              </FormGroup>
              </Col>
              <Col md={3}>
              <FormGroup>
                <Label>
                Purchase type
                </Label>
                <Input
                 type='select'
                 id='purchase_type'
                 name='purchase_type'
                  className={classnames({ 'is-invalid': errors['purchase_type'] })}
                  innerRef={register({ required: true, validate: value => value !== '' })}
                placeholder='Enter Your material sub category'
                 autoFocus>
                {PurchaseType && PurchaseType.data && PurchaseType.data.data && PurchaseType.data.data.map((materialinfo, index) => {
                   console.log(materialinfo)
                   return (
                    <option value ={materialinfo._id}>{materialinfo.document_type_name}</option>
                    )
                  })}
                  </Input>
              </FormGroup>
              </Col>
              <Col md={3}>
              <FormGroup>
                <Label>
                purchase Request Date
                </Label>
                <Input
                 type='date'
                 id='tex'
                 name='purchase_request_date'
                  className={classnames({ 'is-invalid': errors['purchase_request_date'] })}
                  innerRef={register({ required: true, validate: value => value !== '' })}
                placeholder='Enter Your tax category'
                 autoFocus />
              </FormGroup>
              {errors.purchase_request_date && errors.purchase_request_date.type === 'required' && <p style={{color:"red"}}>Purchase request date is required</p>}
              </Col>
              <Col md={3}>
              <FormGroup>
                <Label>
                Department
                </Label>
                <Input
                 type='text'
                 id='department'
                 name='department'
                  className={classnames({ 'is-invalid': errors['department'] })}
                  innerRef={register({ required: true, validate: value => value !== '' })}
                placeholder='Enter Your department'
                 autoFocus />
              </FormGroup>
              {errors.department && errors.department.type === 'required' && <p style={{color:"red"}}>department tax is required</p>}
              </Col>
              <Col md={3}>
              <FormGroup>
                <Label>
                validity date
                </Label>
                <Input
                 type='date'
                 id='validity_date'
                 name='validity_date'
                  className={classnames({ 'is-invalid': errors['validity_date'] })}
                  innerRef={register({ required: true, validate: value => value !== '' })}
                placeholder='Enter Your validity date'
                 autoFocus />
              </FormGroup>
              {errors.validity_date && errors.validity_date.type === 'required' && <p style={{color:"red"}}>validity date is required</p>}
              </Col>
              <Col md={3}>
              <FormGroup>
                <Label>
                Required date
                </Label>
                <Input
                 type='date'
                 id='required_date'
                 name='required_date'
                  className={classnames({ 'is-invalid': errors['required_date'] })}
                  innerRef={register({ required: true, validate: value => value !== '' })}
                 autoFocus />
              </FormGroup>
              {errors.required_date && errors.required_date.type === 'required' && <p style={{color:"red"}}>Required date is required</p>}
              </Col>
              <Col md={3}>
              <FormGroup>
                <Label>
                Remark
                </Label>
                <Input
                 type='text'
                 id='remark'
                 name='remark'
                  className={classnames({ 'is-invalid': errors['remark'] })}
                  innerRef={register({ required: true, validate: value => value !== '' })}
                placeholder='Enter Your remark'
                 autoFocus />
              </FormGroup>
              {errors.remark && errors.remark.type === 'required' && <p style={{color:"red"}}>Remark tax is required</p>}
              </Col>

              <Col md={3}>
              <FormGroup>
                <Label>
                attachment file
                </Label>
                <Input
                 type='file'
                 id='attachment_file'
                 name='attachment_file'
                 onChange={(e) => uploadImage(e)}
                  className={classnames({ 'is-invalid': errors['attachment_file'] })}
                  innerRef={register({ required: true, validate: value => value !== '' })}
                placeholder='Enter Your attachment_file'
                 autoFocus />
              </FormGroup>
              {errors.attachment_file && errors.attachment_file.type === 'required' && <p style={{color:"red"}}>attachment file tax is required</p>}
              </Col>
              </Row>
                <div>
                  <Divider style={{backgroundColor:"#EC8A37"}}/>
               </div>   
        <Row>
         {indexes.map(index => {
        const fieldName = `purchase_item_details[${index}]`
        return (
          <fieldset name={fieldName} key={fieldName}>
          <Col md={12}>
            <h5 className={classes.titleColor}>  Item Detail </h5>
           </Col>
           <Row>
              <Col md={3}>
              <FormGroup>
                <Label>
                Item description
                </Label>
                <Input
                 type='text'
                 id='item_description'
                 name={`${fieldName}.item_description`}
                  className={classnames({ 'is-invalid': errors['item_description'] })}
                  innerRef={register({ required: true, validate: value => value !== '' })}
                placeholder='Enter Your Designation'
                 autoFocus />
              </FormGroup>
              {errors.item_description && errors.item_description.type === 'required' && <p style={{color:"red"}}>Item description is required</p>}
              </Col>
              <Col md={3}>
              <FormGroup>
                <Label>
                Quantity
                </Label>
                <Input
                 type='number'
                 id='quantity'
                 name={`${fieldName}.quantity`}
                  className={classnames({ 'is-invalid': errors['quantity'] })}
                  innerRef={register({ required: true, validate: value => value !== '' })}
                placeholder='Enter Your Designation'
                 autoFocus />
              </FormGroup>
              {errors.quantity && errors.quantity.type === 'required' && <p style={{color:"red"}}>quantity is required</p>}
              </Col>
              <Col md={3}>
              <FormGroup>
                <Label>
                 uom
                </Label>
                <Input
                 type='select'
                 id='uom'
                 name={`${fieldName}.uom`}
                  className={classnames({ 'is-invalid': errors['uom'] })}
                  innerRef={register({ required: true, validate: value => value !== '' })}
                placeholder='Enter Your primary uom'
                 autoFocus >
                {primaryuom && primaryuom.data && primaryuom.data.data && primaryuom.data.data.map((uom, index) => {
                   return (
                    <option value ={uom._id}>{uom.uom_description}</option>
                    )
                  })}
                  </Input>
              </FormGroup>
              </Col>
              <Col md={3}>
              <FormGroup>
                <Label>
                Rate
                </Label>
                <Input
                 type='text'
                 id='rate'
                 name={`${fieldName}.rate`}
                  className={classnames({ 'is-invalid': errors['rate'] })}
                  innerRef={register({ required: true, validate: value => value !== '' })}
                placeholder='Enter Your mangaged type'
                 autoFocus />
              </FormGroup>
              {errors.rate && errors.rate.type === 'required' && <p style={{color:"red"}}>Rate is required</p>}
              </Col>
              <Col md={3}>
              <FormGroup>
                <Label>
                Tax rate
                </Label>
                <Input
                 type='number'
                 id='tax_rate'
                 name={`${fieldName}.tax_rate`}
                  className={classnames({ 'is-invalid': errors['tax_rate'] })}
                  innerRef={register({ required: true, validate: value => value !== '' })}
                placeholder='Enter Your Designation'
                 autoFocus />
              {errors.tax_rate && errors.tax_rate.type === 'required' && <p style={{color:"red"}}>Tax rate is required</p>}
              </FormGroup>
              </Col>
              <Col md={3}>
              <FormGroup>
                <Label>
                Remarks
                </Label>
                <Input
                 type='text'
                 id='remarks'
                 name={`${fieldName}.remarks`}
                  className={classnames({ 'is-invalid': errors['remarks'] })}
                  innerRef={register({ required: true, validate: value => value !== '' })}
                placeholder='Enter Your remarks'
                 autoFocus />
              </FormGroup>
              {errors.remarks && errors.remarks.type === 'required' && <p style={{color:"red"}}>Remarks tax is required</p>}
              </Col>
              <Col md={3}>
              <FormGroup>
                <Label>
                Total Amount
                </Label>
                <Input
                 type='text'
                 id='total_amount'
                 name={`${fieldName}.total_amount`}
                  className={classnames({ 'is-invalid': errors['total_amount'] })}
                  innerRef={register({ required: true, validate: value => value !== '' })}
                placeholder='Enter Your Designation'
                 autoFocus />
              </FormGroup>
              {errors.total_amount && errors.total_amount.type === 'required' && <p style={{color:"red"}}>total amount is required</p>}
              </Col>
              {/* <Col md={3}>
              <FormGroup>
                <Label>
                Status
                </Label>
                <Input
                 type='select'
                 id='status'
                 name='status'
                  className={classnames({ 'is-invalid': errors['status'] })}
                  innerRef={register({ required: true, validate: value => value !== '' })}
                placeholder='Enter Your status'
                 autoFocus>
                 <option value={true}>True</option>
                 <option value={false}>False</option>
                 </Input>
              </FormGroup>
              {errors.status && errors.status.type === 'required' && <p style={{color:"red"}}>Status tax is required</p>}
              </Col> */}
              </Row>
               <Divider style={{backgroundColor:"#EC8A37"}}/>
                 </fieldset>
                )
              })} 
               <Col md={12}>
               <Button.Ripple color="success"
               // onClick={handleAddAgenda}
               onClick={addFriend}
               size="sm"
                style={{ color: green[500], float:"right", margin:"5px" }}
               >
                 Add Purchase Item
               </Button.Ripple>  
                  {/* <Icon className="fa fa-plus-circle" onClick={handleAddAgenda}  style={{ color: green[500], fontSize:"35px", float:"right" }}/> */}
             </Col>

              <Col md={12}>
              <span className="input-group-btn">
          <Button.Ripple  type="submit" className='ripple-button  btn-theam theam_m' size="sm" style={{float:"right"}}>
          { props.preloadValue ? 'Update' : 'Submit' }
              </Button.Ripple>
              </span>
              </Col>
            </Row>
            </Form>
    </div>
  </div>
)
}

const PurchaseMaster = (props) => {
  const [addform, setAddForm] = useState(false)
  const [updateuser, setUpdateuser] = useState(false)
  const [addbutton, setAddButton] = useState(false)
  const [userdetail, setUserDetail] = useState([])
  const [user, setUser] = useState([])
  const classes = useStyles()
  const [page, setPage] = useState(0)
  const [rowsPerPage, setRowsPerPage] = useState(10)
  const [searching, setSearchStream] = useState('')
  const handleChangePage = (event, newPage) => {
    setPage(newPage)
  }

  const handleChangeRowsPerPage = (event) => {
    setRowsPerPage(+event.target.value)
    setPage(0)
  }
 ///search table data
const searchChange = (e) => {
 e.preventDefault()
 const searchstream = e.target.value
//  setSearchStream(searchstream)
//  const searchString = searching
if (searchstream.length > 3) {
  const newstring = new RegExp(searchstream, 'gi')
       const searchitem = userdetail && userdetail.data && userdetail.data.data && userdetail.data.data.filter(item => {

         return item.vendor_name.match(newstring) 
       })
       console.log(searchitem)
       setUserDetail({data:{data:searchitem}})
      } else {
        setUserDetail(user)
      }
}
  const onAddClick = () => {
    setAddForm(true)
    setAddButton(true)
    setUpdateuser(false)
  }
  const onCancleClick = () => {
    setAddForm(false)
    setAddButton(false)
  }

  const onEditClick = (detail) => {
    props.history.push({
      state : {
   detail
     }})
     setUpdateuser(true)
     setAddForm(false)
  }
  const userdetailapi = 'http://172.105.49.15:3030/purchase/list_purchase_master'
  useEffect(() => {
    // app.auth().currentUser.getIdToken(false).then(function (Token) {
      axios.get(userdetailapi)
      .then(r => {
        setUser(r)
        setUserDetail(r)
      })
    // })

  }, [addform, updateuser])

  useEffect(() => {
    const {location, history} = props
    //use the state via location.state
    //and replace the state via
    history.replace() 
  }, [history])
console.log(userdetail)
  return (
    <div className='auth-wrapper auth-v1 px-2'>
      <div className='auth-inner py-2'>
      { addform === true ?   <Card className='mb-0'>
          <CardBody>
            <CardTitle tag='h4' className='mb-1'>
            Purchase Request
            </CardTitle>
            {/* <CardText className='mb-2'>Please Register for master</CardText> */}
           <MasterForm onAdd={() => { setAddForm(false) }} />
          </CardBody>
        </Card> : " "}
        {updateuser === true ?   <Card className='mb-0'>
          <CardBody>
            <CardTitle tag='h4' className='mb-1'>
            Purchase Request
            </CardTitle>
            {/* <CardText className='mb-2'>Please Register for master</CardText> */}
            <MasterForm
             preloadValue={props && props.location && props.location.state} onAdd={() => { setUpdateuser(false) }}
             />
          </CardBody>
        </Card> : "" }
      
        <div style={{padding:10}}>
     
        </div>
        <Card className='mb-0'>
          <CardBody> 
          <Row>
              <Col md={3}>
              <h4 style={{margin:'10px'}} className='mb-1'>Purchase Request</h4>
              </Col>
              <Col md={2}>
                  <h6 style={{float:"right", marginTop:"10px"}}>Search</h6>
                  </Col>
              <Col md={3}>
            <FormGroup>
                <Input
                 type='search'
                 onChange = {(e) => searchChange(e)}
                 id='code'
                name="image"
                //   className={classnames({ 'is-invalid': errors['image'] })}
                //   innerRef={register({ required: true, validate: value => value !== '' })}
                 placeholder='search...' autoFocus />
              </FormGroup>
              </Col>
              <Col md={4}>
              {addbutton === false ?   <Button.Ripple className='ripple-button' type="submit" size="sm" onClick={onAddClick} style={{margin:'10px', float:"right"}}>
          <i class="fa fa-plus"></i> Add Purchase Request
              </Button.Ripple> : <Button.Ripple className='ripple-button' type="submit" size="sm" onClick={onCancleClick} style={{margin:'10px', float:"right"}}>
          <i class="fa fa-minus"></i> Add Purchase Request
              </Button.Ripple>
              
            }
              </Col>
            </Row>
  
    <TableContainer className={classes.container}>
        <Table stickyHeader size="small" aria-label="a dense table">
          <TableHead>
            <TableRow>
              {columns.map((column) => (
                <TableCell
                  key={column.id}
                  align={column.align}
                  style={{ minWidth: column.minWidth }}
                >
                  {column.label}
                </TableCell>
              ))}
            </TableRow>
          </TableHead>
          <TableBody>
            {userdetail && userdetail.data && userdetail.data.data && userdetail.data.data.slice((page * rowsPerPage), (page * rowsPerPage) + rowsPerPage).map((detail, index) => {
              console.log(detail)
              return (
                <TableRow hover role="checkbox" tabIndex={-1}>
                      <TableCell>
                    {index + 1}
                      </TableCell>
                      <TableCell>
                      {detail.location === null ? 'Not found' : detail.location.location_name}
                      </TableCell>
                      <TableCell>
                      {detail.purchase_type === null ? 'Not found' : detail.purchase_type.document_type_name}
                      </TableCell>
                      <TableCell>
                      {detail.currency === null ? 'Not found' : detail.currency.currency_description}
                      </TableCell>
                      <TableCell>
                      {detail.vendor_name}
                      </TableCell>
                      <TableCell>
                      {moment(detail.purchase_request_date).format("DD-MM-YYYY")}
                      </TableCell>
                      <TableCell>
                      {moment(detail.validity_date).format("DD-MM-YYYY")}
                      </TableCell>
                      <TableCell>
                      {moment(detail.required_date).format("DD-MM-YYYY")}
                      </TableCell>
                      <TableCell>
                      {detail.item_description}
                      </TableCell>
                      <TableCell>
                      {detail.quantity}
                      </TableCell>
                      <TableCell>
                      {/* {detail.uom === null ? 'Not Found' : detail.uom.purchase_item_details.uom_description} */}
                      </TableCell>
                      <TableCell>
                      {detail.rate}
                      </TableCell>
                      <TableCell>
                      {detail.tax_rate}
                      </TableCell>
                      <TableCell>
                      {detail.remarks}
                      </TableCell>
                      <TableCell>
                      {detail.remark}
                      </TableCell>
                      <TableCell>
                      {detail.attachment_file}
                      </TableCell>
                      <TableCell>
                      {detail.status === true ? <Switch
        checked={true}
        color="primary"
        name="checkedB"
        inputProps={{ 'aria-label': 'primary checkbox' }}
      /> : <Switch
      checked={false}
      name="checkedA"
      inputProps={{ 'aria-label': 'secondary checkbox' }}
    />}
                      </TableCell>
                      {/* <TableCell>
                      <Button.Ripple className='btn-icon btn-sm rounded-circle' color='flat-success'  onClick={() => onEditClick(detail, props)} outline id={`positionTop_${index + 1}`}>
        <Edit2 size={16} />
      </Button.Ripple>
      <UncontrolledTooltip placement='top' target={`positionTop_${index + 1}`}>
       Edit
      </UncontrolledTooltip>
                      </TableCell> */}
                </TableRow>
              )
            })}
          </TableBody>
        </Table>
      </TableContainer>
      <TablePagination
        rowsPerPageOptions={[10, 20, 30]}
        component="div"
        count={userdetail && userdetail.data && userdetail.data.data && userdetail.data.data.length}
        rowsPerPage={rowsPerPage}
        page={page}
        onPageChange={handleChangePage}
        onRowsPerPageChange={handleChangeRowsPerPage}
      />

    </CardBody>
    </Card>
      </div>
    </div>
  )
}

export default PurchaseMaster
