import {React, useState, useContext, Fragment, useEffect } from 'react'
import {Col, Row, Card, CardBody, CardTitle, CardText, Form, FormGroup, Label, Input, InputGroup, InputGroupAddon, UncontrolledTooltip, InputGroupText, CustomInput, Button } from 'reactstrap'
const moment = require('moment')
import { useForm, Controller } from 'react-hook-form'
import { Edit2 } from 'react-feather'

import classnames from 'classnames'
import { useDispatch } from 'react-redux'
import { add_organization_manpower, update_organization_manpower } from '@store/actions/hr/manpower'
import '@styles/base/pages/page-auth.scss'
import 'bootstrap/dist/css/bootstrap.min.css'
import axios from 'axios'
import { makeStyles } from '@material-ui/core/styles'

import Table from '@material-ui/core/Table'
import TableBody from '@material-ui/core/TableBody'
import TableCell from '@material-ui/core/TableCell'
import TableContainer from '@material-ui/core/TableContainer'
import TableHead from '@material-ui/core/TableHead'
import TablePagination from '@material-ui/core/TablePagination'
import TableRow from '@material-ui/core/TableRow'


import Flatpickr from 'react-flatpickr'
import "flatpickr/dist/themes/material_green.css"
import { data } from 'jquery'


const columns = [
  { id: 'index', label: 'Sr. no.', minWidth: 100 },
  {
    // id: 'name',
    label: 'Organization ',
    minWidth: 170,
    align: 'right'
    },
  {
    label: 'Date',
    minWidth: 170,
    align: 'right'
  },
  {
    label: 'Staff Count',
    minWidth: 170,
    align: 'right'
  },
  {
    label: 'Staff Present',
    minWidth: 170,
    align: 'right'
  },
  {
    label: 'On Rolls Direct Count',
    minWidth: 170,
    align: 'right'
  },
  {
    label: 'On Rolls Direct Present',
    minWidth: 170,
    align: 'right'
  },
  {
    label: 'On Rolls Indirect Count',
    minWidth: 170,
    align: 'right'
  },
  {
    label: 'On Rolls Indirect Present',
    minWidth: 170,
    align: 'right'
  },
  {
    label: 'Contract-P Direct Count',
    minWidth: 170,
    align: 'right'
  },
  {
    label: 'Contract-P Direct Present',
    minWidth: 170,
    align: 'right'
  },
  {
    label: 'Contract-P Indirect Count',
    minWidth: 170,
    align: 'right'
  },
  {
    label: 'Contract-P Indirect Present',
    minWidth: 170,
    align: 'right'
  },
  {
    label: 'Contract 6M Direct Count',
    minWidth: 170,
    align: 'right'
  },
  {
    label: 'Contract 6M Direct Present',
    minWidth: 170,
    align: 'right'
  },
  {
    label: 'Contract 6M Indirect Count',
    minWidth: 170,
    align: 'right'
  },
  {
    label: 'Contract 6M Indirect Present',
    minWidth: 170,
    align: 'right'
  },
  {
    label: 'NAPS Direct Count',
    minWidth: 170,
    align: 'right'
  },
  {
    label: 'NAPS Direct Present',
    minWidth: 170,
    align: 'right'
  },
  {
    label: 'NAPS Indirect Count',
    minWidth: 170,
    align: 'right'
  },
  {
    label: 'NAPS Indirect Present',
    minWidth: 170,
    align: 'right'
  },
  {
    label: 'New Requirement Direct',
    minWidth: 170,
    align: 'right'
  },
  {
    label: 'New Requirement Indirect',
    minWidth: 170,
    align: 'right'
  },
  {
    label: 'Reduction Direct',
    minWidth: 170,
    align: 'right'
  },
  {
    label: 'Reduction Indirect',
    minWidth: 170,
    align: 'right'
  },

  { label: 'ACTION', minWidth: 100 }
]

const useStyles = makeStyles({
  root: {
    width: '100%'
  },
  container: {
    maxHeight: 440
  }
})

const MasterForm = (props) => {
  const { register, errors, setValue, reset, handleSubmit } = useForm({defaultValues:props ? props && props.preloadValue && props.preloadValue.detail : ""})
  // const [email, setEmail] = useState('')
  const dispatch = useDispatch()
  const [organization, setOrganization] = useState('')
  const [picker, setPicker] = useState(new Date())


  // console.log(picker)
//-----------------------submit data ---------------------------------//
const onSubmit = (data, e) => {
  e.preventDefault()
  console.log(data, props)
  if (props && props.preloadValue) { 
    dispatch(update_organization_manpower(data, props))
    // reset(props)
  } else {
    const newItem = Object.assign({}, data, { date: picker })
     dispatch(add_organization_manpower(newItem, props))
  }
  }

  useEffect(() => {
    console.log(props)
        const listItems = props && props.preloadValue && props.preloadValue.detail && [props.preloadValue.detail].map((data, i) => {
          const keys_name = Object.keys(data)
          const keys_value = Object.values(data)
          // console.log({lable:Object.keys(data), value : Object.values(data) })
          const key_data = keys_name.map((info, i) => {
            // console.log({lablee:info, value : keys_value[i] })
            setValue(info, keys_value[i])

          })
      //  console.log(Object.keys(data[i]), Object.values(data[i]))
    })
   setPicker(props && props.preloadValue && props.preloadValue.detail.date)
    // setValue("organization_id", props && props.preloadValue && props.preloadValue.detail.organization_id)

    // console.log("key",, )
   // ) 
// const info = props && props.preloadValue && props.preloadValue.detail.map((data, index) => {

// console.log(listItems)
//    const info = props && props.preloadValue && props.preloadValue.detail.map((data, index) => {
// console.log(data)
      // return (

      //   // <option value={org._id}>{org.location_name}</option>
      // )
    // })
// setValue("organization_id", props && props.preloadValue && props.preloadValue.detail.organization_id)
// setValue("staff_count", props && props.preloadValue && props.preloadValue.detail.staff_count)
 
}, [props])

  //-------------------get  organization list -------------------------------------//
  const organizations = 'http://172.105.49.15:3030/api/master/list_organization'
