const Man_Power = (props) => {
    const { register, errors, reset,  setValue, handleSubmit, control } = useForm({defaultValues: props ? props && props.preloadValue && props.preloadValue.detail : ""})
    // const [attendies, setAttendies] = useState(false)
    // const [department, setDepartment] = useState('')
    // const [personName, setPersonName] = useState([])
    // const [stuff, setStuff] = useState([])
    const [add_form, setAddForm] = useState(false)
    const [update_form, setUpdateForm] = useState(false)
    const [add_button, setAddButton] = useState(false)

    const classes = useStyles()
    const dispatch = useDispatch()
    const [manpower, setManpower_list] = useState('')
    const [manpower_info, setManpower] = useState([])
    const [page, setPage] = useState(0)
    const [rowsPerPage, setRowsPerPage] = useState(10)
    const [searching, setSearchStream] = useState('')

    // props.history.push({
    //   state : {detail}})
//----------------page change---------------------------------//
    const handleChangePage = (event, newPage) => {
      setPage(newPage)
    }
  //------------------------row change-----------------------------//
    const handleChangeRowsPerPage = (event) => {
      setRowsPerPage(+event.target.value)
      setPage(0)
    }
  //--------------------search data-------------------------------------------------//
  
        const searchTable = (e) => {
          e.preventDefault()
          const search_string = e.target.value
        if (search_string.length > 0) {
          const new_string = new RegExp(search_string, 'g')
                const search_item = manpower && manpower.data.filter(item => {
                  return item.staff_count.toString().match(new_string)
                })
                setManpower_list({data:search_item})
              } else {
                setManpower_list(manpower_info)
              }
        }

//--------------------get  list of manpower---------------------------------//
        const man_power_list = 'http://172.105.49.15:3030/api/list_manpower'
          useEffect(() => {
            axios.get(man_power_list)
            .then(data => { 
              setManpower(data.data)
              setManpower_list(data.data)
              }) 
          }, [add_form, update_form])

//---------------------button change event ------------------------------------//

const onAddClick = () => {
  setAddForm(true)
  setAddButton(true)
  setUpdateForm(false)
}
const onCancelClick = () => {
  setAddForm(false)
  setAddButton(false)
}

//---------------------on edit ----------------------------------------------//
const onEditClick = (detail) => {

  props.history.push({
    state : {detail }})

   setUpdateForm(true)
   setAddForm(false)
}

//---------------------component render ----------------------------------------//
return (
