              </Button.Ripple> : <Button.Ripple className='ripple-button' type="submit" size="sm" onClick={onCancelClick} style={{margin:'10px', float:"right"}}>
          <i class="fa fa-minus"></i> Add Man Power Attendees
              </Button.Ripple>
              
            }
              </Col>
            </Row>
        
            <Row>
                <TableContainer className={classes.container}>
