// ** React Imports
import { Fragment, useEffect, useState } from 'react'
import { useParams } from 'react-router-dom'

// ** Email App Component Imports
import Mails from './Mails'
import Sidebar from './Sidebar'
import Navbar from './Navbar'
// ** Third Party Components
import classnames from 'classnames'
import { useNavbarType } from '@hooks/useNavbarType'
import { useFooterType } from '@hooks/useFooterType'
import { useNavbarColor } from '@hooks/useNavbarColor'

// ** Store & Actions
import { useDispatch, useSelector } from 'react-redux'
import {
  getTopics,
  selectMail,
  updateMails,
  paginateMail,
  selectAllMail,
  resetSelectedMail,
  selectCurrentMail,
  updateMailLabel,
  gettreads
} from './store/actions'

// ** Styles
import '@styles/react/apps/app-email.scss'
import { handleMenuCollapsed, handleContentWidth, handleMenuHidden } from '@store/actions/layout'

const EmailApp = () => {
  // ** States
  const [query, setQuery] = useState('')
  const [sidebarOpen, setSidebarOpen] = useState(false)
  const [composeOpen, setComposeOpen] = useState(false)
  const [navbarType, setNavbarType] = useNavbarType()
  const [footerType, setFooterType] = useFooterType()
  const [navbarColor, setNavbarColor] = useNavbarColor()
  const [windowWidth, setWindowWidth] = useState(window.innerWidth)
  // ** Toggle Compose Function
  const toggleCompose = () => setComposeOpen(!composeOpen)
  const handleWindowWidth = () => {
    setWindowWidth(window.innerWidth)
  }

  
  //** Sets Window Size & Layout Props
  useEffect(() => {
    if (window !== undefined) {
      window.addEventListener('resize', handleWindowWidth)
    }
  }, [windowWidth])
  const layoutStore = useSelector(state => state.layout)
    // ** Vars
    const location = useLocation()
    const contentWidth = layoutStore.contentWidth
    const menuCollapsed = layoutStore.menuCollapsed
    const isHidden = layoutStore.menuHidden
  
    // ** Toggles Menu Collapsed
    const setMenuCollapsed = val => dispatch(handleMenuCollapsed(val))
  // ** Store Variables
  const dispatch = useDispatch()
  const store = useSelector(state => state.email)

  // ** Vars
  const params = useParams()

  // ** UseEffect: GET initial data on Mount
  useEffect(() => {
    dispatch(getTopics({ q: query || '', folder: params.folder || 'inbox', label: params.label || '' }))
  }, [query, params.folder, params.label])

    // ** Vars
    const footerClasses = {
      static: 'footer-static',
      sticky: 'footer-fixed',
      hidden: 'footer-hidden'
    }
  
    const navbarWrapperClasses = {
      floating: 'navbar-floating',
      sticky: 'navbar-sticky',
      static: 'navbar-static',
      hidden: 'navbar-hidden'
    }
  
    const navbarClasses = {
      floating: 'floating-nav',
      sticky: 'fixed-top',
      static: 'navbar-static-top',
      hidden: 'd-none'
    }
  
    const bgColorCondition = navbarColor !== '' && navbarColor !== 'light' && navbarColor !== 'white'
  return (
    <Fragment>
          <div
      className={classnames(
        `wrapper vertical-layout ${navbarWrapperClasses[navbarType] || 'navbar-floating'} ${
          footerClasses[footerType] || 'footer-static'
        }`,
        {
          // Modern Menu
          'vertical-menu-modern': windowWidth >= 1200,
          'menu-collapsed': menuCollapsed && windowWidth >= 1200,
          'menu-expanded': !menuCollapsed && windowWidth > 1200,

          // Overlay Menu
          'vertical-overlay-menu': windowWidth < 1200,
          'menu-hide': !menuVisibility && windowWidth < 1200,
          'menu-open': menuVisibility && windowWidth < 1200
        }
      )}
      {...(isHidden ? { 'data-col': '1-column' } : {})}
    >
      <Navbar />
      <Sidebar
        store={store}
        dispatch={dispatch}
        getTopics={getTopics}
        gettreads={gettreads}
        sidebarOpen={sidebarOpen}
        toggleCompose={toggleCompose}
        setSidebarOpen={setSidebarOpen}
        resetSelectedMail={resetSelectedMail}
      />
      <div className='content-right'>
        <div className='content-body'>
          <div
            className={classnames('body-content-overlay', {
              show: sidebarOpen
            })}
            onClick={() => setSidebarOpen(false)}
          ></div>
          <Mails
            store={store}
            query={query}
            setQuery={setQuery}
            dispatch={dispatch}
            getTopics={getTopics}
            selectMail={selectMail}
            updateMails={updateMails}
            composeOpen={composeOpen}
            paginateMail={paginateMail}
            selectAllMail={selectAllMail}
            toggleCompose={toggleCompose}
            setSidebarOpen={setSidebarOpen}
            updateMailLabel={updateMailLabel}
            selectCurrentMail={selectCurrentMail}
            resetSelectedMail={resetSelectedMail}
          />
        </div>
      </div>
      </div>
    </Fragment>
  )
}

export default EmailApp
