// ** Third Party Components
import classnames from 'classnames'
import { Link, useParams } from 'react-router-dom'
import PerfectScrollbar from 'react-perfect-scrollbar'
import { Mail, Send, Edit2, Star, Info, Trash } from 'react-feather'
import { Button, ListGroup, ListGroupItem, Badge} from 'reactstrap'
import Breadcrumbs from '@material-ui/core/Breadcrumbs'
import Typography from '@material-ui/core/Typography'
// import Link from '@material-ui/core/Link';
// import Stack from '@material-ui/core/Stack';
// import NavigateNextIcon from '@mui/icons-material/NavigateNext';

import ListItemButton from '@material-ui/core/ListItemButton';
import ListItemIcon from '@material-ui/core/ListItemIcon';
import ListItemText from '@material-ui/core/ListItemText';
import Collapse from '@material-ui/core/Collapse';
import InboxIcon from '@material-ui/core/icons-material/MoveToInbox';
import DraftsIcon from '@material-ui/core/icons-material/Drafts';
import SendIcon from '@material-ui/core/icons-material/Send';
import ExpandLess from '@material-ui/core/icons-material/ExpandLess';
import ExpandMore from '@material-ui/core/icons-material/ExpandMore';
import StarBorder from '@material-ui/core/icons-material/StarBorder';


const Sidebar = props => {
  // ** Props
  const { store, sidebarOpen, toggleCompose, dispatch, getMails, resetSelectedMail, setSidebarOpen } = props
  const [open, setOpen] = React.useState(true);

  const handleClick = () => {
    setOpen(!open);
  };
  // ** Vars
  const params = useParams()

  // ** Functions To Handle Folder, Label & Compose
  const handleFolder = folder => {
    dispatch(getMails({ ...store.params, folder }))
    dispatch(resetSelectedMail())
  }

  const handleLabel = label => {
    dispatch(getMails({ ...store.params, label }))
    dispatch(resetSelectedMail())
  }

  const handleComposeClick = () => {
    toggleCompose()
    setSidebarOpen(false)
  }

  // ** Functions To Active List Item
  const handleActiveItem = value => {
    if ((params.folder && params.folder === value) || (params.label && params.label === value)) {
      return true
    } else {
      return false
    }
  }

  return (
    <div
      className={classnames('sidebar-left', {
        show: sidebarOpen
      })}
    >
      <div className='sidebar'>
        <div className='sidebar-content email-app-sidebar'>
          <div className='email-app-menu'>
            <div className='form-group-compose text-center compose-btn'>
              {/* <Button.Ripple className='compose-email' color='primary' block onClick={handleComposeClick}>
                Compose
              </Button.Ripple> */}
               <List
      sx={{ width: '100%', maxWidth: 360, bgcolor: 'background.paper' }}
      component="nav"
      aria-labelledby="nested-list-subheader"
      subheader={
        <ListSubheader component="div" id="nested-list-subheader">
          Nested List Items
        </ListSubheader>
      }
    >
      <ListItemButton>
        <ListItemIcon>
          <SendIcon />
        </ListItemIcon>
        <ListItemText primary="Sent mail" />
      </ListItemButton>
      <ListItemButton>
        <ListItemIcon>
          <DraftsIcon />
        </ListItemIcon>
        <ListItemText primary="Drafts" />
      </ListItemButton>
      <ListItemButton onClick={handleClick}>
        <ListItemIcon>
          <InboxIcon />
        </ListItemIcon>
        <ListItemText primary="Inbox" />
        {open ? <ExpandLess /> : <ExpandMore />}
      </ListItemButton>
      <Collapse in={open} timeout="auto" unmountOnExit>
        <List component="div" disablePadding>
          <ListItemButton sx={{ pl: 4 }}>
            <ListItemIcon>
              <StarBorder />
            </ListItemIcon>
            <ListItemText primary="Starred" />
          </ListItemButton>
        </List>
      </Collapse>
    </List>
            </div>
            <PerfectScrollbar className='sidebar-menu-list' options={{ wheelPropagation: false }}>
              <ListGroup tag='div' className='list-group-messages'>
                <ListGroupItem
                 tag={Link}
                  to='/apps/email/inbox'
                  onClick={() => handleFolder('inbox')}
                  action
                  active={!Object.keys(params).length || handleActiveItem('inbox')}
                >
                  <h5>Thread Name HR 2022 Trends</h5>
                  <span className='broadcom_align'>
                    <Breadcrumbs separator="|" aria-label="breadcrumb">
                      <Link underline="hover" key="1" color="inherit" href="/" > 21 New Topic</Link>
                      <Link   key="2"  href="/getting-started/installation/"> 23 topics </Link>
                      <Link  key="2" color="inherit" href="/getting-started/installation/"> 5 Members </Link>
                  </Breadcrumbs>
                    <span className='align-middle'>{store.emailsMeta.draft ? (
                      <Badge className='float-right bg-danger' color='white' pill >
                        {/* {store.emailsMeta.draft} */}
                        23 </Badge>) : null}</span>
                    </span>
                </ListGroupItem>

                <ListGroupItem
                 tag={Link}
                  to='/apps/email/inbox'
                  onClick={() => handleFolder('inbox')}
                  action
                >
                  <h5>Thread Name HR 2022 Trends</h5>
                  <span className='broadcom_align'>
                    <Breadcrumbs separator="|" aria-label="breadcrumb">
                      <Link underline="hover" key="1" color="inherit" href="/" > 21 New Topic</Link>
                      <Link   key="2"  href="/getting-started/installation/"> 23 topics </Link>
                      <Link  key="2" color="inherit" href="/getting-started/installation/"> 5 Members </Link>
                  </Breadcrumbs>
                    <span className='align-middle'>{store.emailsMeta.draft ? (
                      <Badge className='float-right bg-danger' color='white' pill >
                        {/* {store.emailsMeta.draft} */}
                        23 </Badge>) : null}</span>
                    </span>
                </ListGroupItem>
                <ListGroupItem
                 tag={Link}
                  to='/apps/email/inbox'
                  onClick={() => handleFolder('inbox')}
                  action
                >
                  <h5>Thread Name HR 2022 Trends</h5>
                  <span className='broadcom_align'>
                    <Breadcrumbs separator="|" aria-label="breadcrumb">
                      <Link underline="hover" key="1" color="inherit" href="/" > 21 New Topic</Link>
                      <Link   key="2"  href="/getting-started/installation/"> 23 topics </Link>
                      <Link  key="2" color="inherit" href="/getting-started/installation/"> 5 Members </Link>
                  </Breadcrumbs>
                    <span className='align-middle'>{store.emailsMeta.draft ? (
                      <Badge className='float-right bg-danger' color='white' pill >
                        {/* {store.emailsMeta.draft} */}
                        23 </Badge>) : null}</span>
                    </span>
                </ListGroupItem>
              </ListGroup>
            </PerfectScrollbar>
          </div>
        </div>
      </div>
    </div>
  )
}

export default Sidebar
