import { Link } from 'react-router-dom'
import { Facebook, Twitter, Mail, GitHub } from 'react-feather'
import InputPasswordToggle from '@components/input-password-toggle'
import { Card, CardBody, CardTitle, CardText, Form, FormGroup, Label, Input, CustomInput, Button } from 'reactstrap'
import '@styles/base/pages/page-auth.scss'
import logo  from '../../../Images/logo.svg'

const LoginV1 = () => {
  return (
    <div className='auth-wrapper auth-v1 px-2'>
      <div className='auth-inner py-2'>
        <Card className='mb-0'>
          <CardBody>
            <Link className='brand-logo' to='/' onClick={e => e.preventDefault()}>
              <img src={logo}  height="40px" ></img>
            {/* {logo} */}
              {/* <h2 className='brand-text text-primary ml-1'>Vuexy</h2> */}
            </Link>
            {/* <CardTitle tag='h4' className='mb-1'>
              Welcome to Vuexy! ðŸ‘‹
            </CardTitle> */}
            {/* <CardText className='mb-2'>Please sign-in to your account and start the adventure</CardText> */}

            <Form className='auth-login-form mt-2' onSubmit={e => e.preventDefault()}>
              <FormGroup>
                <Label className='form-label' for='login-email'>
                  Email
                </Label>
                <Input type='email' id='login-email' placeholder='Enter your registered email-id' autoFocus />
              </FormGroup>
              <FormGroup>
                <div className='d-flex justify-content-between'>
                  <Label className='form-label' for='login-password'>
                    Password
                  </Label>
                </div>
                <InputPasswordToggle className='input-group-merge' id='login-password' />
              </FormGroup>
              <FormGroup className="d-flex">
                <CustomInput type='checkbox' className='custom-control-Primary' id='remember-me' label='' /><label style={{color:"#ed1d25" }}>By continuing you agree to <Link to='/pages'>
                <span>Terms & Conditions</span> </Link></label>
              </FormGroup>
              <Button.Ripple color='primary' block>
                Sign in
              </Button.Ripple>
            </Form>
          </CardBody>
        </Card>
      </div>
    </div>
  )
}

export default LoginV1
