import { useState, useContext, Fragment, useEffect } from 'react'
import classnames from 'classnames'
import Avatar from '@components/avatar'
import { useSkin } from '@hooks/useSkin'
import useJwt from '@src/auth/jwt/useJwt'
import { useDispatch } from 'react-redux'
import { useForm } from 'react-hook-form'
import { toast, Slide } from 'react-toastify'
import { SendOtp } from '@store/actions/auth'
import { AbilityContext } from '@src/utility/context/Can'
import { Link, useHistory } from 'react-router-dom'
import InputPasswordToggle from '@components/input-password-toggle'
import { withRouter } from 'react-router'
import { getHomeRouteForLoggedInUser, isObjEmpty } from '@utils'
import { Facebook, Twitter, Mail, GitHub, HelpCircle, Coffee } from 'react-feather'
import {
  Alert,
  Row,
  Col,
  CardTitle,
  CardText,
  Form,
  Input,
  FormGroup,
  Label,
  CustomInput,
  Button,
  UncontrolledTooltip
} from 'reactstrap'
import axios from 'axios'
import '@styles/base/pages/page-auth.scss'

const ToastContent = ({ name, role }) => (
  <Fragment>
    <div className='toastify-header'>
      <div className='title-wrapper'>
        <Avatar size='sm' color='success' icon={<Coffee size={12} />} />
        <h6 className='toast-title font-weight-bold'>Welcome, {name}</h6>
      </div>
    </div>
    <div className='toastify-body'>
      <span>You have successfully logged in as an {role}. Now you can start to explore. Enjoy!</span>
    </div>
  </Fragment>
)

const OtpCreator = props => {
  const [skin, setSkin] = useSkin()
  const ability = useContext(AbilityContext)
  const dispatch = useDispatch()
  const history = useHistory()
  const [phone, setPhone] = useState('')
  const [count, setCount] = useState(5)
  const [password, setOtp] = useState('')

  const { register, errors, handleSubmit } = useForm()
  const illustration = skin === 'dark' ? 'login-v2-dark.svg' : 'login-v2.svg',
    source = require(`@src/assets/images/pages/${illustration}`).default
useEffect(() => {
    const intervalId = window.setInterval(() => setCount(counter => counter - 1), 1000)
    if (count === "0") {
      window.clearInterval(intervalId)
    }
    return () => {
        console.log('unmounted', intervalId)
        // window.clearInterval(intervalId)
      } 
}, [])
  const onSubmit = data => {
    dispatch(SendOtp(data))
// history.push('/forgot-password')
  }

  return (
    <div className='auth-wrapper auth-v2'>
      <Row className='auth-inner m-0'>
    
        <Col className='d-flex align-items-center auth-bg px-2 p-lg-5' lg='4' sm='12'>
          <Col className='px-xl-2 mx-auto' sm='8' md='6' lg='12'>
            <CardText className='mb-2'>Please enter your otp</CardText>
            <Form className='auth-login-form mt-2' onSubmit={handleSubmit(onSubmit)}>
              <FormGroup>
                <div className='d-flex justify-content-between'>
                  <Label className='form-label' for='login-password'>
                    OTP
                  </Label>
                  {/* <Link to='/forgot-password'>
                    <small>Forgot Password?</small>
                  </Link> */}
                </div>
                <Input
                  value={password}
                  id='login_otp'
                  name='login_otp'
                  className='input-group-merge'
                  placeholder="Enter Your OTP"
                  onChange={e => setOtp(e.target.value)}
                  className={classnames({ 'is-invalid': errors['login_otp'] })}
                  innerRef={register({ required: true, validate: value => value !== '' })}
                />
              </FormGroup>
              {/* <FormGroup>
                <CustomInput type='checkbox' className='custom-control-Primary' id='remember-me' label='Remember Me' />
              </FormGroup> */}
              <Button.Ripple type='submit' color='primary' block>
                OTP
              </Button.Ripple>
            </Form>
            <p className='text-right mt-2'>
              {/* <span className='mr-25'>New on our platform?</span> */}
              {/* <Link to='/register'> */}
              {/* <Link to='/forgot-password'>
                <span>Forgot Password ?</span>
              </Link> */}
            </p>

          </Col>
        </Col>
            <Col className='d-none d-lg-flex align-items-center p-5' lg='8' sm='12'>
          <div className='w-100 d-lg-flex align-items-center justify-content-center px-5'>
              <h1>{count}</h1>
            <img className='img-fluid' src={source} alt='Login V2' />
          </div>
        </Col>
      </Row>
    </div>
  )
}

export default withRouter(OtpCreator)
