    format: (value) => value.toLocaleString('en-US')
  },
  {
    id: 'esic_no',
    label: 'Esic no',
    minWidth: 170,
    align: 'right',
    format: (value) => value.toLocaleString('en-US')
  },
  {
    id: 'pf_no',
    label: 'Pf no',
    minWidth: 170,
    align: 'right',
    format: (value) => value.toLocaleString('en-US')
  },  {
    id: 'uan_no',
    label: 'Uan no',
    minWidth: 170,
    align: 'right',
    format: (value) => value.toLocaleString('en-US')
  },  {
    id: 'basic_rate',
    label: 'Basic rate',
    minWidth: 170,
    align: 'right',
    format: (value) => value.toLocaleString('en-US')
  },  {
    id: 'hra_rate',
    label: 'Hra rate',
    minWidth: 170,
    align: 'right',
    format: (value) => value.toLocaleString('en-US')
  },  {
    id: 'conveyance_rate',
    label: 'Conveyance rate',
    minWidth: 170,
    align: 'right',
    format: (value) => value.toLocaleString('en-US')
  },  {
    id: 'da_rate',
    label: 'Da rate',
    minWidth: 170,
    align: 'right',
    format: (value) => value.toLocaleString('en-US')
  },
  {
    id: 'medical_allowance',
    label: 'Medical allowance',
    minWidth: 170,
    align: 'right',
    format: (value) => value.toLocaleString('en-US')
  },
  {
    id: 'leave_with_wages',
    label: 'Leave with wages',
    minWidth: 170,
    align: 'right',
    format: (value) => value.toLocaleString('en-US')
  },
  {
    id: 'other_allowance',
    label: 'Other allowance',
    minWidth: 170,
    align: 'right',
    format: (value) => value.toLocaleString('en-US')
  },
  {
    id: 'bonus',
    label: 'Bonus',
    minWidth: 170,
    align: 'right',
    format: (value) => value.toLocaleString('en-US')
  },
  {
    id: 'transport_allowance',
    label: 'Transport allowance',
    minWidth: 170,
    align: 'right',
    format: (value) => value.toLocaleString('en-US')
  },
  {
    id: 'canteen',
    label: 'Canteen',
    minWidth: 170,
    align: 'right',
    format: (value) => value.toLocaleString('en-US')
  },
  {
    id: 'bus',
    label: 'Bus',
    minWidth: 170,
    align: 'right',
    format: (value) => value.toLocaleString('en-US')
  },
  { id: 'action', label: 'Action', minWidth: 100 }
]


const useStyles = makeStyles({
  root: {
    width: '100%'
  },
  container: {
    height: "100%"
  },
  educationalImage: {
    borderRadius:"50%",
    marginRight:"5px",  
    "&:hover": {
      zIndex: 2,
      transform: "scale(9)"
    }
  }
})

const MasterForm = (props) => {
    const { register, errors, setValue, reset, handleSubmit } = useForm({defaultValues:props ? props && props.preloadValue && props.preloadValue.detail : "",  shouldUnregister: false})
    const [EducationalUrl, setEducationalUrl] = useState([])
    const [ContractorList, setContractorList] = useState([])
    const [updatedImage, setUpdatedPanImage] = useState(props && props.preloadValue && props.preloadValue.detail)
    const [updatedAadharImage, setUpdatedAadharImage] = useState(props && props.preloadValue && props.preloadValue.detail)
    const [updatedEducationalImage, setUpdatedEducationalImage] = useState(props && props.preloadValue && props.preloadValue.detail.educational_certificates_url)
    const [bucketimage, setUploadPanUrl] = useState()
    const [AadharImage, setAadharImage] = useState()
    const [EducationalImage, setEducationalImage] = useState()
    const dispatch = useDispatch()

console.log(EducationalUrl)
    useEffect(() => {
      // if (props && props.preloadValue && props.preloadValue.detail.pan_url) {
      //   setValue("pan_url", props && props.preloadValue && props.preloadValue.detail.pan_url)
      // }
      // if (props && props.preloadValue && props.preloadValue.detail.aadhar_url) {
      //   setValue("aadhar_url", props && props.preloadValue && props.preloadValue.detail.aadhar_url)
      // }

      setValue("date_of_birthday", moment(props && props.preloadValue && props.preloadValue.detail.date_of_birthday).format("YYYY-MM-DD"))
      setValue("date_of_joining", moment(props && props.preloadValue && props.preloadValue.detail.date_of_joining).format("YYYY-MM-DD"))
      if (props && props.preloadValue && props.preloadValue.detail.educational_certificates_url) {
      setValue("educational_certificates_url", props && props.preloadValue && props.preloadValue.detail.educational_certificates_url)
      }
    }, [setValue])

//image upload 
const uploadPanUrl = (e) => {
  console.log(e.target.files[0])
  uploadFile(e.target.files[0], config)
.then(data => {
  setUploadPanUrl(data)
  setValue("pan_url", data.location)
})
.catch(err => console.error(err))
}

const uploadAadharUrl = (e) => {
  console.log(e.target.files[0])
  uploadFile(e.target.files[0], config)
.then(data => {
  setAadharImage(data)
  setValue("aadhar_url", data.location)
})
.catch(err => console.error(err))
}


  const uploadEducationalUrl = (e) => {
    if (props && props.preloadValue && props.preloadValue.detail) {
    console.log(e.target.files[0])
    uploadFile(e.target.files[0], config)
  .then(data => {
    // setEducationalImage([data])
    setUpdatedEducationalImage([...updatedEducationalImage, data.location])
    setValue("educational_certificates_url", [...updatedEducationalImage, data.location])
  })
  .catch(err => console.error(err))
  } else {
    console.log(e.target.files[0])
    uploadFile(e.target.files[0], config)
  .then(data => {
    setEducationalImage([data])
    setEducationalUrl([...EducationalUrl, data.location])
    setValue("educational_certificates_url", [...EducationalUrl, data.location])
  })
  .catch(err => console.error(err))
  }
  }


    const contractors = 'http://172.105.49.15:3030/web/list_contractor'
    useEffect(() => {
      axios.get(contractors)
      .then(r => setContractorList(r))
    }, [setContractorList])

//delete certificates
const DeleteCertificates = (i) => {
  const deletedCertificate = updatedEducationalImage.filter((educationcertificate, index) => {
                  return updatedEducationalImage.indexOf(educationcertificate) !== i
  })

  console.log(deletedCertificate)
    setUpdatedEducationalImage(deletedCertificate)

    // setEducationalImage(deletedCertificate)
    // setEducationalUrl([...EducationalUrl, deletedCertificate])

  setValue("educational_certificates_url", [...updatedEducationalImage, deletedCertificate])
}

const DeleteAddedCertificates = (i) => {
  const deletedCertificate = EducationalUrl.filter((educationcertificate, index) => {
                  return EducationalUrl.indexOf(educationcertificate) !== i
  })

  console.log(deletedCertificate)
    setEducationalImage(deletedCertificate)
    setEducationalUrl([deletedCertificate])

  setValue("educational_certificates_url", [...EducationalUrl, deletedCertificate])
}
    //HANDLE SUBMIT
    const onSubmit = data => {
      console.log(data)
        if (props && props.preloadValue) { 
          dispatch(update_EmployeRegistration(data, props))
        } else {
          dispatch(add_EmployeRegistration(data, props))
        }
      }

    // console.log(props)
return (
  <div>
  <Form className='auth-login-form mt-2' onSubmit={handleSubmit(onSubmit)}>
            <Row>  
          <Col md={3}>
            <FormGroup>
                <Label className='form-label'>
                Contractor
                </Label>
                <Input type='select' id='contractor_id'
                name="contractor_id"
                  className={classnames({ 'is-invalid': errors['contractor_id'] })}
                  innerRef={register({ required: true, validate: value => value !== '' })}
                 placeholder='Enter your contractor' autoFocus>
                   {ContractorList && ContractorList.data && ContractorList.data.data && ContractorList.data.data.map((detail, index) => {
                     return (
                          <option value={detail._id}>{detail.contractors_name}</option>
                     )
                   })}
                 </Input>
              </FormGroup>
      {errors.contractor_id && errors.contractor_id.type === 'required' && <p style={{color:"red"}}>location code is required</p>}
              </Col>

              <Col md={3}>
            <FormGroup>
                <Label className='form-label'>
                Labourer type
                </Label>
                <Input type='text' id='labourer'
                name="labourer"
                  className={classnames({ 'is-invalid': errors['labourer'] })}
                  innerRef={register({ required: true, validate: value => value !== '' })}
                 placeholder='Enter your labourer' autoFocus />
              </FormGroup>
      {errors.labourer && errors.labourer.type === 'required' && <p style={{color:"red"}}>location code is required</p>}
              </Col>

              <Col md={3}>
            <FormGroup>
                <Label className='form-label'>
                Name
                </Label>
                <Input type='text' id='name'
                name="name"
                  className={classnames({ 'is-invalid': errors['name'] })}
                  innerRef={register({ required: true, validate: value => value !== '' })}
                 placeholder='Enter your name' autoFocus />
              </FormGroup>
      {errors.name && errors.name.type === 'required' && <p style={{color:"red"}}>location code is required</p>}
              </Col>

              <Col md={3}>
            <FormGroup>
                <Label className='form-label'>
                Gender
                </Label>
                <Input type='select' id='gender'
                name="gender"
                  className={classnames({ 'is-invalid': errors['gender'] })}
                  innerRef={register({ required: true, validate: value => value !== '' })}
                 placeholder='Enter your gender' autoFocus >
                   <option>Male</option>
                   <option>Female</option>
                   <option>Other</option>
                   </Input>
              </FormGroup>
      {errors.gender && errors.gender.type === 'required' && <p style={{color:"red"}}>location code is required</p>}
              </Col>

              <Col md={3}>
            <FormGroup>
                <Label className='form-label'>
                Address
                </Label>
                <Input type='textarea' id='address'
                name="address"
                  className={classnames({ 'is-invalid': errors['address'] })}
                  innerRef={register({ required: true, validate: value => value !== '' })}
                 placeholder='Enter your address' autoFocus />
              </FormGroup>
      {errors.address && errors.address.type === 'required' && <p style={{color:"red"}}>location code is required</p>}
              </Col>

              <Col md={3}>
            <FormGroup>
                <Label className='form-label'>
                PAN
                </Label>
                <Input type='text' id='pan'
                name="pan"
                  className={classnames({ 'is-invalid': errors['pan'] })}
                  innerRef={register({ required: true, validate: value => value !== '' })}
                 placeholder='Enter your pan' autoFocus />
              </FormGroup>
      {errors.pan && errors.pan.type === 'required' && <p style={{color:"red"}}>location code is required</p>}
              </Col>

              {(bucketimage && bucketimage.location) || (updatedImage) ? <Col md={1}>       
                  <Button close style={{backgroundColor:"red", borderRadius:"50%"}} onClick={() => {
                    setUploadPanUrl("")
                    setUpdatedPanImage("")
                }}/>
                <img src={(bucketimage && bucketimage.location) || (props && props.preloadValue && props.preloadValue.detail && props.preloadValue.detail.pan_url)} width="80px" height="80px" alt="img"/> 
                </Col> :     <Col md={3}><FormGroup>
                <Label className='form-label' for='login-email'>
                PAN Url
                </Label>
                <CustomInput type="file"
                 id="exampleCustomFileBrowser"
                 name="pan_url"
                 onChange={(e) => uploadPanUrl(e)}
                 className={classnames({ 'is-invalid': errors['pan_url'] })}
                 innerRef={register("pan_url", { required: true, validate: value => value !== '' })}
                />
              </FormGroup>
              {errors.pan_url && errors.pan_url.type === 'required' && <p style={{color:"red"}}>PAN Url is required</p>}
              </Col> 
}


              <Col md={3}>
            <FormGroup>
                <Label className='form-label'>
                Aadhar
                </Label>
                <Input type='text' id='aadhar'
                name="aadhar"
                  className={classnames({ 'is-invalid': errors['aadhar'] })}
                  innerRef={register({ required: true, validate: value => value !== '' })}
                 placeholder='Enter your aadhar' autoFocus />
              </FormGroup>
      {errors.aadhar && errors.aadhar.type === 'required' && <p style={{color:"red"}}>location code is required</p>}
              </Col>

              {(AadharImage && AadharImage.location) || (updatedAadharImage) ? <Col md={1}>       
                  <Button close style={{backgroundColor:"red", borderRadius:"50%"}} onClick={() => {
                    setAadharImage("")
                    setUpdatedAadharImage("")
                }}/>
                <img src={(AadharImage && AadharImage.location) || (props && props.preloadValue && props.preloadValue.detail && props.preloadValue.detail.aadhar_url)} width="80px" height="80px" alt="img"/> 
                </Col> : <Col md={3}><FormGroup>
                <Label className='form-label' for='login-email'>
                Aadhar url
                </Label>
                <CustomInput type="file"
                 id="exampleCustomFileBrowser"
                 name="aadhar_url"
                 onChange={(e) => uploadAadharUrl(e)}
                 className={classnames({ 'is-invalid': errors['aadhar_url'] })}
                 innerRef={register("aadhar_url", { required: true, validate: value => value !== '' })}
                />
              </FormGroup>
              {errors.aadhar_url && errors.aadhar_url.type === 'required' && <p style={{color:"red"}}>aadhar url is required</p>}
              </Col> 
}


              <Col md={3}>
              <FormGroup inline>
        <Label for="exampleCustomFileBrowser">Educational certificates url</Label>
        <CustomInput type="file"
         id="exampleCustomFileBrowser"
         name="educational_certificates_url"
         onChange={(e) => uploadEducationalUrl(e)}
         className={classnames({ 'is-invalid': errors['educational_certificates_url'] })}
         defaultValues={[]}
        innerRef={register("educational_certificates_url", { required: true, validate: value => value !== '' })}
         label="Yo, pick a file!" />
      </FormGroup>
      {errors.educational_certificates_url && errors.educational_certificates_url.type === 'required' && <p style={{color:"red"}}>location code is required</p>}
      {updatedEducationalImage ? <Col md={1} style={{display:"flex"}}>
                  {updatedEducationalImage.map((img, index) => {
                  return (
                    <div>
                <Button close style={{backgroundColor:"red", borderRadius:"50%"}} onClick={() => {
                  DeleteCertificates(index)
                    // setEducationalImage("")
                    // setUpdatedEducationalImage("")
                }}/>
                    <img src={img} width="60px" height="60px" alt="img"/>
                    </div>
                  )
                })}
                 </Col> : <Col md={1} style={{display:"flex"}}>
      {EducationalUrl && EducationalUrl.map((img, index) => {
        console.log(img)
                  return (
                    <div>
                <Button close style={{backgroundColor:"red", borderRadius:"50%"}} onClick={() => {
                  DeleteAddedCertificates(index)
                    // setEducationalImage("")
                    // setUpdatedEducationalImage("")
                }}/>
                    <img src={img} width="60px" height="60px" alt="img"/>
                    </div>
                  )
                }) }
                </Col> }
              </Col>

              <Col md={3}>
            <FormGroup>
                <Label className='form-label'>
                Date of birthday
                </Label>
                <Input type='date' id='date_of_birthday'
                name="date_of_birthday"
                  className={classnames({ 'is-invalid': errors['date_of_birthday'] })}
                  innerRef={register({ required: true, validate: value => value !== '' })}
                 placeholder='Enter your dob' autoFocus />
              </FormGroup>
      {errors.date_of_birthday && errors.date_of_birthday.type === 'required' && <p style={{color:"red"}}>location code is required</p>}
              </Col>

              <Col md={3}>
            <FormGroup>
                <Label className='form-label'>
                Date of joining
                </Label>
                <Input type='date' id='date_of_joining'
                name="date_of_joining"
                  className={classnames({ 'is-invalid': errors['date_of_joining'] })}
                  innerRef={register({ required: true, validate: value => value !== '' })}
                 placeholder='Enter your date of joining' autoFocus />
              </FormGroup>
      {errors.date_of_joining && errors.date_of_joining.type === 'required' && <p style={{color:"red"}}>location code is required</p>}
              </Col>
              <Col md={12}>
                 <Divider style={{backgroundColor:"#EC8A37"}}/>
               </Col> 
               <Col md={12}>
                <h5>Bank Details</h5>
               </Col>
              <Col md={3}>
            <FormGroup>
                <Label className='form-label' for='login-micr'>
                Bank name
                </Label>
                <Input type='text' id='bank_name'
                name="bank_name"
                  className={classnames({ 'is-invalid': errors['bank_name'] })}
                  innerRef={register({ required: true, validate: value => value !== '' })}
                 placeholder='Enter your bank name' autoFocus />
              </FormGroup>
      {errors.bank_name && errors.bank_name.type === 'required' && <p style={{color:"red"}}>location code is required</p>}
              </Col>

              <Col md={3}>
            <FormGroup>
                <Label className='form-label'>
               Bank Account name
                </Label>
                <Input type='text' id='account_name'
                name="account_name"
                  className={classnames({ 'is-invalid': errors['account_name'] })}
                  innerRef={register({ required: true, validate: value => value !== '' })}
                 placeholder='Enter your account name' autoFocus />
              </FormGroup>
      {errors.account_name && errors.account_name.type === 'required' && <p style={{color:"red"}}>location code is required</p>}
              </Col>

              <Col md={3}>
            <FormGroup>
                <Label className='form-label'>
                IFSC code
                </Label>
                <Input type='text' id='ifsc_code'
                name="ifsc_code"
                  className={classnames({ 'is-invalid': errors['ifsc_code'] })}
                  innerRef={register({ required: true, validate: value => value !== '' })}
                 placeholder='Enter your ifsc code' autoFocus />
              </FormGroup>
      {errors.ifsc_code && errors.ifsc_code.type === 'required' && <p style={{color:"red"}}>location code is required</p>}
              </Col>

              <Col md={3}>
            <FormGroup>
                <Label className='form-label'>
                MICR
                </Label>
                <Input type='text' id='micr'
                name="micr"
                  className={classnames({ 'is-invalid': errors['micr'] })}
                  innerRef={register({ required: true, validate: value => value !== '' })}
                 placeholder='Enter your micr' autoFocus />
              </FormGroup>
      {errors.micr && errors.micr.type === 'required' && <p style={{color:"red"}}>location code is required</p>}
              </Col>

              <Col md={3}>
            <FormGroup>
                <Label className='form-label'>
                Branch
                </Label>
                <Input type='text' id='branch'
                name="branch"
                  className={classnames({ 'is-invalid': errors['branch'] })}
                  innerRef={register({ required: true, validate: value => value !== '' })}
                 placeholder='Enter your branch' autoFocus />
              </FormGroup>
      {errors.branch && errors.branch.type === 'required' && <p style={{color:"red"}}>location code is required</p>}
              </Col>

              <Col md={3}>
            <FormGroup>
                <Label className='form-label'>
                Association
                </Label>
