         item.bank_name.match(newstring) || item.account_name.match(newstring) || item.ifsc_code.match(newstring) || item.micr.match(newstring) || item.branch.match(newstring) || 
         item.category.match(newstring) || item.esic_no.toString().match(newstring) || item.pf_no.toString().match(newstring) || item.uan_no.toString().match(newstring) || 
         item.basic_rate.toString().match(newstring) || item.hra_rate.toString().match(newstring) || item.conveyance_rate.toString().match(newstring) || item.da_rate.toString().match(newstring) || 
         item.medical_allowance.toString().match(newstring) || item.leave_with_wages.toString().match(newstring) || item.other_allowance.toString().match(newstring) || 
         item.bonus.toString().match(newstring) || item.transport_allowance.toString().match(newstring) || item.canteen.toString().match(newstring) || item.bus.toString().match(newstring) 
       })
       console.log(searchitem)
       setUserDetail({data:{data:searchitem}})
