sourcesContent:
import {React, useState, useContext, Fragment, useEffect } from 'react'
import {Col, Row, Card, CardBody, CardTitle, CardText, Form, FormGroup, Label, Input, InputGroup, InputGroupAddon, InputGroupText, CustomInput, Button } from 'reactstrap'
const moment = require('moment')
import { useForm, Controller } from 'react-hook-form'
import classnames from 'classnames'
import { isObjEmpty, selectThemeColors } from '@utils'
import { ArrowLeft, ArrowRight } from 'react-feather'
import axios from 'axios'
import Select from 'react-select'
import Flatpickr from 'react-flatpickr'
import "flatpickr/dist/themes/material_green.css"

const Implementation_readiness = ({ stepper, type }) => {
  // const { register, errors, handleSubmit, trigger } = useForm()
  const { register, errors, control, setValue, reset, handleSubmit } = useForm()

  const [users, setUsers] = useState('')

  const onSubmit = (data, e) => {
    e.preventDefault()
    console.log(data)
    
    // trigger()
    // if (isObjEmpty(errors)) {
      stepper.next()
    // }
  }

//   //-------------------get  user list -------------------------------------//
const user_list = 'http://172.105.49.15:3030/api/get_active_users'
useEffect(() => {
  axios.get(user_list)
  .then(data => setUsers(data.data)    
    )
}, [users])

  return (
    <Fragment>
      {/* <div className='content-header'>
        <h5 className='mb-0'>Disposal Recommendation</h5>
        <small>Enter Disposal Recommendation.</small>
      </div> */}
      <Form  onSubmit={handleSubmit((data, e) => onSubmit(data, e))}>
      <Row>
              <Col md={6}>
                
              <Row>
              <Col md={2}>
                    <FormGroup style={{marginTop:"10px"}}>
                    <Label className='form-label' for='' style={{fontWeight:"600", fontSize:"14px", color: "#3271ef"}}>Product</Label>
                    </FormGroup>
                </Col>
                <Col md={3}>
                <FormGroup>
                    <Label className='form-label' for='product_effect_of_ecr'>Effect Of ECR<span  style={{color:"red"}} className="required">*</span></Label>
                    <Input type='select'  name="product_effect_of_ecr"  id="product_effect_of_ecr"
                      className={classnames({ 'is-invalid': errors['product_effect_of_ecr'] })}
                      innerRef={register({ required: true, validate: value => value !== '' })}
                    placeholder='Enter ' autoFocus>
                    <option value="">Select Option</option>
                    <option value="Yes">Yes</option>
                    <option value="No">No</option>
                    </Input>
                {errors.product_effect_of_ecr && errors.product_effect_of_ecr.type === 'required' && <span style={{color:"red"}}>Please select effect of ECR </span>}
                  </FormGroup>
              </Col>

              <Col md={3}>
              <Fragment>
                <Label for='date-time-picker'>Target Date</Label>
               <Controller
                as={
                <Flatpickr name="product_effect_of_date" />}
                    className='form-control'
                    name="product_effect_of_date"
                    control={control}
                    placeholder='DD-MM-YYYY '
                    ref={register("product_effect_of_date", { required: true })}
                    options={{ dateFormat: "d-m-Y" }}
                    />
                {errors.product_effect_of_date && errors.product_effect_of_date.type === 'required' && <span style={{color:"red"}}>Please select target date </span>}
                </Fragment>               
              </Col>
              <Col md={4}>
                <FormGroup>
                    <Label className='form-label' for='product_resp_user_id'>Resp<span  style={{color:"red"}} className="required">*</span></Label>
                  <Controller
                    control={control}
                    name="product_resp_user_id"
                      as={
                      <Select  
                        theme={selectThemeColors}
                        ref={register("product_resp_user_id", { required: true, validate: value => value !== '' })}
                        className={classnames({ 'is-invalid': errors['product_resp_user_id']})}
                        classNamePrefix='select option'
                        options={ users && users.data && users.data.map((u, index) => {
                          return  { value : u._id, label : u.name }
                        })}
                        isClearable={true}
                />
              }
               />
          {errors.product_resp_user_id && errors.product_resp_user_id.type === 'required' && <span style={{color:"red"}}>Please select responsibility </span>}

          </FormGroup>
              </Col>

              </Row>
{/* ------------------------------------------------child part -------------------------------------------- */}
          </Col>
          <Col md={6}>
              <Row>
              <Col md={2}>
                    <FormGroup style={{ marginTop:"30px"}}>
                    <Label className='form-label' for='' style={{fontWeight:"600", fontSize:"14px", color: "#3271ef"}}>Child Part</Label>
                    </FormGroup>
                </Col>
                <Col md={3}>
                <FormGroup>
                    <Label className='form-label' for='child_part_effect_of_ecr'>Effect Of ECR<span  style={{color:"red"}} className="required">*</span></Label>
                    <Input type='select'  name="child_part_effect_of_ecr"  id="child_part_effect_of_ecr"
                      className={classnames({ 'is-invalid': errors['child_part_effect_of_ecr'] })}
                      innerRef={register({ required: true, validate: value => value !== '' })}
                    placeholder='Enter ' autoFocus>
                    <option value="">Select Option</option>
                    <option value="Yes">Yes</option>
                    <option value="No">No</option>
                    </Input>
                {errors.child_part_effect_of_ecr && errors.child_part_effect_of_ecr.type === 'required' && <span style={{color:"red"}}>Please select effect of ECR </span>}
                  </FormGroup>
              </Col>
              <Col md={3}>
              <Fragment>
                <Label for='date-time-picker'>Target Date</Label>
               <Controller
                as={
                <Flatpickr name="product_effect_of_date" />}
                    className='form-control'
                    name="product_effect_of_date"
                    control={control}
                    placeholder='DD-MM-YYYY '
                    ref={register("product_effect_of_date", { required: true })}
                    options={{ dateFormat: "d-m-Y" }}
                    />
                {errors.product_effect_of_date && errors.product_effect_of_date.type === 'required' && <span style={{color:"red"}}>Please select target date </span>}
                </Fragment>               
             
              </Col>
             
              <Col md={4}>
                <FormGroup>
                    <Label className='form-label' for='child_part_resp_user_id'>Responsibility<span  style={{color:"red"}} className="required">*</span></Label>
                  <Controller
                    control={control}
                    name="child_part_resp_user_id"
                      as={
                      <Select  
                        theme={selectThemeColors}
                        ref={register("child_part_resp_user_id", { required: true, validate: value => value !== '' })}
                        className={classnames({ 'is-invalid': errors['child_part_resp_user_id']})}
                        classNamePrefix='select option'
                        options={ users && users.data && users.data.map((u, index) => {
                          return  { value : u._id, label : u.name }
                        })}
                        isClearable={true}
                />
              }
               />
          {errors.child_part_resp_user_id && errors.child_part_resp_user_id.type === 'required' && <span style={{color:"red"}}>Please select responsibility </span>}

          </FormGroup>
              </Col>

              </Row>
</Col>
              {/* ------------------------------------------------Bill of material  -------------------------------------------- */}
              <Col md={6}>
              <Row>
              <Col md={2}>
                    <FormGroup style={{ marginTop:"10px"}}>
                    <Label className='form-label' for='' style={{fontWeight:"600", fontSize:"14px", color: "#3271ef"}}>Bill Of Material</Label>
                    </FormGroup>
                </Col>
                <Col md={3}>
                <FormGroup>
                    <Label className='form-label' for='bill_off_material_effect_of_ecr'>Effect Of ECR<span  style={{color:"red"}} className="required">*</span></Label>
                    <Input type='select'  name="bill_off_material_effect_of_ecr"  id="bill_off_material_effect_of_ecr"
                      className={classnames({ 'is-invalid': errors['bill_off_material_effect_of_ecr'] })}
                      innerRef={register({ required: true, validate: value => value !== '' })}
                    placeholder='Enter ' autoFocus>
                    <option value="">Select Option</option>
                    <option value="Yes">Yes</option>
                    <option value="No">No</option>
                    </Input>
                {errors.bill_off_material_effect_of_ecr && errors.bill_off_material_effect_of_ecr.type === 'required' && <span style={{color:"red"}}>Please select effect of ECR </span>}
                  </FormGroup>
              </Col>
              <Col md={3}>
              <Fragment>
                <Label for='date-time-picker'>Target Date</Label>
               <Controller
                as={
                <Flatpickr name="product_effect_of_date" />}
                    className='form-control'
                    name="product_effect_of_date"
                    control={control}
                    placeholder='DD-MM-YYYY '
                    ref={register("product_effect_of_date", { required: true })}
                    options={{ dateFormat: "d-m-Y" }}
                    />
                {errors.product_effect_of_date && errors.product_effect_of_date.type === 'required' && <span style={{color:"red"}}>Please select target date </span>}
                </Fragment>               
              </Col>
             <Col md={4}>
                <FormGroup>
                    <Label className='form-label' for='bill_off_material_resp_user_id'>Responsibility<span  style={{color:"red"}} className="required">*</span></Label>
                  <Controller
                    control={control}
                    name="bill_off_material_resp_user_id"
                      as={
                      <Select  
                        theme={selectThemeColors}
                        ref={register("bill_off_material_resp_user_id", { required: true, validate: value => value !== '' })}
                        className={classnames({ 'is-invalid': errors['bill_off_material_resp_user_id']})}
                        classNamePrefix='select option'
                        options={ users && users.data && users.data.map((u, index) => {
                          return  { value : u._id, label : u.name }
                        })}
                        isClearable={true}
                />
              }
               />
          {errors.bill_off_material_resp_user_id && errors.bill_off_material_resp_user_id.type === 'required' && <span style={{color:"red"}}>Please select responsibility </span>}

          </FormGroup>
              </Col>

              </Row>
              
              </Col>
              {/* ------------------------------------------------Test Specs -------------------------------------------- */}
              <Col md={6}>
              <Row>
              <Col md={2}>
                    <FormGroup style={{ marginTop:"30px"}}>
                    <Label className='form-label' for='' style={{fontWeight:"600", fontSize:"14px", color: "#3271ef"}}>Test Specs</Label>
                    </FormGroup>
                </Col>
                <Col md={3}>
