// ** React Imports
import { Fragment, useEffect, useState } from 'react'
import { useParams } from 'react-router-dom'

// ** Email App Component Imports
import Mails from './Mails'
import Sidebar from './Sidebar'
// import Navbar from './Navbar'
// ** Third Party Components
import classnames from 'classnames'

// ** Store & Actions
import { useDispatch, useSelector } from 'react-redux'
import {
  getTopics,
  selectMail,
  updateMails,
  paginateMail,
  selectAllMail,
  resetSelectedMail,
  selectCurrentMail,
  updateMailLabel,
  gettreads
} from './store/actions'

// ** Styles
import '@styles/react/apps/app-email.scss'

const EmailApp = () => {
  // ** States
  const [query, setQuery] = useState('')
  const [sidebarOpen, setSidebarOpen] = useState(true)
  const [composeOpen, setComposeOpen] = useState(false)

  const [reloadthread, setReloadthread] = useState(false)
  // ** Toggle Compose Function
  const toggleCompose = () => setComposeOpen(!composeOpen)

  // ** Store Variables
  const dispatch = useDispatch()
  const store = useSelector(state => state.email)

  // ** Vars
  const params = useParams()

  // ** UseEffect: GET initial data on Mount
  useEffect(() => {
   
    // params.folder 
    dispatch(getTopics({ q: query || '', folder: params.folder || 'inbox', label: params.label || '' }))
  }, [query, params.folder, params.label])

  return (
    <Fragment>
      {/* <Navbar /> */}
      <Sidebar
        store={store}
        dispatch={dispatch}
        getTopics={getTopics}
        gettreads={gettreads}
        sidebarOpen={sidebarOpen}
        toggleCompose={toggleCompose}
        setSidebarOpen={setSidebarOpen}
        resetSelectedMail={resetSelectedMail}
        setReloadthread={setReloadthread}
        reloadthread={reloadthread}

        
  />
  
      <div className='content-right'>
        <div className='content-body'>
          <div
            // className={classnames('body-content-overlay', {
            //   show: sidebarOpen
            // })}
            onClick={() => setSidebarOpen(false)}>

          </div>
          <Mails
            store={store}
            query={query}
            setQuery={setQuery}
            dispatch={dispatch}
            getTopics={getTopics}
            selectMail={selectMail}
            updateMails={updateMails}
            composeOpen={composeOpen}
            paginateMail={paginateMail}
            selectAllMail={selectAllMail}
            toggleCompose={toggleCompose}
            setSidebarOpen={setSidebarOpen}
            updateMailLabel={updateMailLabel}
            selectCurrentMail={selectCurrentMail}
            resetSelectedMail={resetSelectedMail}
            setReloadthread={setReloadthread}

          />
        </div>
      </div>
    </Fragment>
  )
}

export default EmailApp
